import React, { Component, useEffect, useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  TextInput,
  Modal,
  Dimensions,
} from "react-native";
import Icon from "react-native-vector-icons/Entypo";

// Get the full dimensions of the screen
const { width, height } = Dimensions.get('window');

function MultipleTimeSelectableOptionGroup({
  style,
  setopenDropdown,
  openDropdown,
  id,
  label,
  isRequired,
  myObjProfile,
  setmyObjProfile,
  index,
  e,
}) {
  const options = e.optionsList;
  const [localMyObjProfile, setlocalMyObjProfile] = useState(myObjProfile);

  const onMinusPress = ({ option, listIndex }) => {
    const newMyObjProfile = structuredClone(localMyObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs
    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    if (
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes > 0
    ) {
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes -=
        1 * thisItemCountsAs;
    }

    setlocalMyObjProfile(newMyObjProfile);
  };

  const onPlusPress = ({ option, listIndex }) => {
    const newMyObjProfile = structuredClone(localMyObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs

    const selectedItems = newMyObjProfile.options[index].optionsList.filter(
      (op) => op.selectedTimes > 0
    );

    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    let selectedTimesTotal = thisItemCountsAs;

    selectedItems.map((op) => {
      selectedTimesTotal += op.countsAs
        ? parseInt(op.selectedTimes) * parseInt(op.countsAs)
        : parseInt(op.selectedTimes);
    });

    if (
      parseInt(e.numOfSelectable) >= selectedTimesTotal ||
      !e.numOfSelectable ||
      parseInt(e.numOfSelectable) === 0
    ) {
      if (newMyObjProfile.options[index].optionsList[listIndex].selectedTimes) {
        newMyObjProfile.options[index].optionsList[
          listIndex
        ].selectedTimes += 1;
      } else {
        newMyObjProfile.options[index].optionsList[listIndex].selectedTimes = 1;
      }
      setlocalMyObjProfile(newMyObjProfile);
    } else {
      console.log(
        "Didnt Work ",
        "selectedTimesTotal: ",
        selectedTimesTotal,
        " e.numOfSelectable: ",
        e.numOfSelectable
      );
    }
  };

  const closeDropdownAndUpdate = () => {
    setopenDropdown(null);
    setmyObjProfile(localMyObjProfile);
  };

  return (
    <View style={[styles.container, style]}>
      <Text style={styles.lbl}>
        {label} {isRequired ? "*" : ""}
      </Text>
      <View>
        <TouchableOpacity
          style={styles.dropdown}
          onPress={() => {
            console.log("openDropdown", openDropdown, id);
            if (openDropdown === id) {
              closeDropdownAndUpdate();
            } else {
              setopenDropdown(id);
            }
          }}
        >
          <Text style={styles.placeholder}>Select {label}</Text>
          <Icon
            name={
              openDropdown === id ? "chevron-small-up" : "chevron-small-down"
            }
            style={styles.downIcon}
          ></Icon>
        </TouchableOpacity>
        {openDropdown === id && (
          <>
            <View style={styles.overlay} pointerEvents="none" />
            <TouchableOpacity
              style={styles.touchableOverlay}
              onPress={closeDropdownAndUpdate}
              activeOpacity={1} // Transparent, only for closing the dropdown
            />
            <ScrollView
              style={styles.scrollView}
              scrollEventThrottle={16}
              pointerEvents="auto" // Reactivate touch events for the dropdown content
            >
              {options.map((option, listIndex) => (
                <View
                  key={listIndex}
                  id={option.id}
                  style={{
                    width: "100%",
                    height: 44,
                    backgroundColor: "white",
                    padding: 10,
                    borderWidth: 1,
                    justifyContent: "space-between",
                    alignItems: "center",
                    flexDirection: "row",
                  }}
                >
                  <TouchableOpacity
                    onPress={() => onMinusPress({ option, listIndex })}
                  >
                    <Text>-</Text>
                  </TouchableOpacity>
                  <View
                    style={{
                      flexDirection: "row",
                      alignItems: "center",
                      justifyContent: "space-between",
                      width: "80%",
                    }}
                  >
                    <Text>
                      {`${option.label} ${
                        option.priceIncrease
                          ? `(+$${option.priceIncrease})`
                          : ""
                      }`}
                    </Text>
                    <TextInput
                      style={{
                        width: 40,
                        height: 30,
                        borderWidth: 1,
                        borderColor: "black",
                        margin: 10,
                      }}
                      value={
                        localMyObjProfile.options[index].optionsList[listIndex]
                          .selectedTimes > 0
                          ? localMyObjProfile.options[index].optionsList[
                              listIndex
                            ].selectedTimes
                          : 0
                      }
                      keyboardType="numeric"
                      // onChangeText={(text) => {
                      //   let newOptions = [...options];
                      //   newOptions[index].selectedTimes = text;
                      //   setoptions(newOptions);
                      // }}
                    />
                  </View>
                  <TouchableOpacity
                    onPress={() => onPlusPress({ option, listIndex })}
                  >
                    <Text>+</Text>
                  </TouchableOpacity>
                </View>
              ))}
            </ScrollView>
          </>
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  lbl: {
    fontWeight: "700",
    color: "#3e3f41",
  },
  dropdown: {
    width: 352,
    height: 44,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
    borderWidth: 2,
    borderColor: "#6987d3",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "flex-start",
  },
  placeholder: {
    fontWeight: "500",
    color: "#7f838c",
    fontSize: 12,
    margin: 10,
  },
  downIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    margin: 0,
    marginTop: 2,
    marginRight: 2,
  },
  fullScreenButton: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "transparent",
  },
  overlay: {
    position: "absolute",
    width: width,
    height: height,
    backgroundColor: "transparent", // Ensure the background is transparent
  },
  touchableOverlay: {
    position: "absolute",
    top: 0,
    left: 0,
    width: width,
    height: height,
    zIndex: 1, // Make sure it's above other content but below the dropdown content
  },
  scrollView: {
    width: 352,
    position: "absolute",
    backgroundColor: "white",
    // ... other styles you may need for the dropdown
    zIndex: 2, // Ensure the dropdown is above the touchable overlay
  },
});

export default MultipleTimeSelectableOptionGroup;
