import React, { Component, useState } from "react";
import { StyleSheet, View, Image, TouchableOpacity } from "react-native";
import { Entypo } from "@expo/vector-icons";
import ActiveDropDownOption from "./ActiveDropDownOption";
import NonActiveDropDownOption from "./NonActiveDropDownOption";

function MenuBtn({ active, labelImg, labelImgStyle, onPress }) {

  return (
      <TouchableOpacity
        style={[
          styles.container,
          active && {
            shadowColor: "rgba(0,0,0,1)",
            shadowOffset: {
              width: 3,
              height: 3,
            },
            elevation: 5,
            shadowOpacity: 0.2,
            shadowRadius: 0,
            borderRadius: 10,
            backgroundColor: "rgba(255,255,255,1)",
          },
        ]}
        onPress={onPress}
        activeOpacity={0.8}
      >
        <Image
          source={labelImg}
          resizeMode="contain"
          style={[styles.btnLblImg, labelImgStyle]}
        />
        <Entypo
          name={
            !active
              ? "chevron-small-right"
              : open
              ? "chevron-small-up"
              : "chevron-small-down"
          }
          style={styles.dropDownBtnChevronDown}
        />
      </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 20,
    height: 42,
    width: 201,
  },
  btnLblImg: {
    marginRight: 10,
    marginLeft: 10,
    resizeMode: "contain",
  },
  dropDownBtnChevronDown: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    marginRight: 10,
    marginLeft: 10,
  },
  dropDownMenuBtn: {
    height: 42,
    width: 201,
  },
  dropDownOptionsContainer: {
    width: 179,
    height: 75,
    justifyContent: "space-between",
    marginBottom: 20,
    marginLeft: 20,
  },
  activeDropDownOption: {
    height: 34,
    width: 179,
  },
  nonActiveDropDownOption: {
    height: 34,
    width: 179,
  },
});

export default MenuBtn;
