import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  ScrollView,
} from "react-native";
import { Button } from "@react-native-material/core";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import ProductOptionDropDown from "./ProductOptionDropDown";
import { addCartState, cartState, setCartState } from "state/state";

const ProductListing = React.memo(({ product, itemIndex, goBack }) => {
  const cart = cartState.use();
  const [myObjProfile, setMyObjProfile] = useState(product);
  const [total, setTotal] = useState(
    myObjProfile.total ? myObjProfile.total : myObjProfile.price
  );
  const [extraInput, setExtraInput] = useState(
    myObjProfile.extraDetails ? myObjProfile.extraDetails : ""
  );



  useEffect(() => {
    setTotal(getPrice());
  }, [myObjProfile]);




  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={goBack} style={styles.backButton}>
          <MaterialCommunityIcons
            name="chevron-left"
            size={32}
            color="#4A4A4A"
          />
          <Text style={styles.dashboardText}>Dashboard</Text>
        </TouchableOpacity>
        <Text style={styles.productName}>{myObjProfile.name}</Text>
        <Text style={styles.totalText}>
          Total: ${parseFloat(total).toFixed(2)}
        </Text>
      </View>
      <ScrollView style={styles.scrollView}>
        {myObjProfile.description && (
          <Text style={styles.description}>
            Description: {myObjProfile.description}
          </Text>
        )}
        {myObjProfile.options.map((e, index) => (
          <DisplayOption e={e} index={index} key={index} />
        ))}
      </ScrollView>
      <View style={styles.footer}>
        <TextInput
          placeholder="Write any extra info here.."
          onChangeText={(val) => setExtraInput(val)}
          value={extraInput}
          style={styles.input}
        />
        <Button
          title={itemIndex >= 0 ? "Save" : "Add To Cart"}
          onPress={AddToCart}
          style={styles.button}
          titleStyle={styles.buttonTitle}
        />
      </View>
    </View>
  );
});

export default ProductListing;

const styles = StyleSheet.create({
  sizeRow: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: 10,
  },
  halfRow: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    width: "50%",
  },
  toppingsContainer: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-between",
    marginBottom: 25,
    marginTop: 25,
  },
  touchable: {
    margin: 25,
    width: 300,
  },
  modalContainer: {
    marginTop: 25,
    marginBottom: 25,
    padding: "5%",
    backgroundColor: "white",
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "rgba(232,232,232,1)",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 30,
    shadowOpacity: 0.03,
    shadowRadius: 10,
    height: "70%",
  },
  btn: {
    marginBottom: 25,
  },
  dropDown: {
    marginBottom: 25,
  },
  h2White: {
    fontSize: 17,
    color: "white",
  },
  h2Black: {
    fontSize: 25,
    color: "black",
    fontWeight: "600",
  },
  multiOption: {
    // backgroundColor: "rgba(205,213,255,1)",
    borderRadius: 10,
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 30,
    shadowOpacity: 0.03,
    shadowRadius: 10,
    alignItems: "center",
    justifyContent: "space-around",
    padding: 15,
    margin: 5,
    minWidth: 150,
  },
  optionNameTxt: {
    fontFamily: "archivo-600",
    fontSize: 18,
  },
  optionPriceTxt: {
    fontFamily: "archivo-600",
    fontSize: 16,
    padding: 10,
  },
});
