import React, { Component } from "react";
import { StyleSheet, TouchableOpacity, Text } from "react-native";

function ActiveOneTimeSelectableBtn(props) {
  return (
    <TouchableOpacity style={[styles.container, props.style]}>
      <Text style={styles.lbl}>Small (+$0)</Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#1a2a51",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center"
  },
  lbl: {
    color: "rgba(221,222,228,1)",
    fontSize: 13
  }
});

export default ActiveOneTimeSelectableBtn;
