import {
  Modal,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  Animated,
  ImageBackground,
} from "react-native";
import React, { useEffect, useRef, useState } from "react";
import { userStoreState } from "state/state";
import { Button, TextInput } from "@react-native-material/core";
import { addCartState } from "state/state";
import ProductOptionDropDown from "./ProductOptionDropDown";
import ProductListing from "./ProductListing";
import { auth, storage } from "state/firebaseConfig";

function useTraceUpdate(props) {
  const prev = useRef(props);
  useEffect(() => {
    const changedProps = Object.entries(props).reduce((ps, [k, v]) => {
      if (prev.current[k] !== v) {
        ps[k] = [prev.current[k], v];
      }
      return ps;
    }, {});
    if (Object.keys(changedProps).length > 0) {
      console.log("Changed props:", changedProps);
    }
    prev.current = props;
  });
}

const ProductDisplayBtn = (props) => {
  const [showProductScreen, setshowProductScreen] = useState(false);
  const xPos = useRef(new Animated.Value(-1000)).current;
  const shadowOpacity = useRef(new Animated.Value(0)).current;
  // const [productImage, setproductImage] = useState();

  // useEffect(() => {
  //   console.log("product: ", product);
  //   if (product.hasImage) {
  //     (async () => {
  //       const url = await getProductUrl();
  //       setproductImage(url);
  //       console.log(url);
  //     })();
  //   }
  // }, []);

  // const getProductUrl = async () =>
  //   await storage
  //     .ref(auth.currentUser.uid + "/images/" + product.id)
  //     .getDownloadURL();

  const fadeIn = () => {
    // Will change xPos value to 0 in 3 seconds
    setshowProductScreen(true);
    Animated.timing(xPos, {
      toValue: 0,
      duration: 200,
      useNativeDriver: false,
    }).start();
    Animated.timing(shadowOpacity, {
      toValue: 1,
      duration: 200,
      useNativeDriver: false,
    }).start();
  };

  const fadeOut = () => {
    // Will change xPos value to 0 in 3 seconds
    Animated.timing(shadowOpacity, {
      toValue: 0,
      duration: 200,
      useNativeDriver: false,
    }).start();
    Animated.timing(xPos, {
      toValue: -1000,
      duration: 200,
      useNativeDriver: false,
    }).start(() => setshowProductScreen(false));
  };

  return (
    <>
      <TouchableOpacity
        key={productIndex}
        onPress={() => {
          if (product.options.length > 0) {
            // navigation.navigate("Product Listing", { product: product });
            // setshowProductScreen(true);
            fadeIn();
          } else {
            addCartState({
              name: product.name,
              price: product.price,
              description: product.description,
              options: [],
              extraDetails: null,
            });
          }
        }}
        style={[styles.touchable, !product.imageUrl && { padding: 10 }]}
      >
        {product.imageUrl ? (
          <ImageBackground
            source={product.imageUrl}
            style={{
              height: "100%",
              width: "100%",
              padding: 10,
              justifyContent: "center",
              alignItems: "center",
              borderRadius: 3,
            }}
            imageStyle={{ opacity: 0.5 }}
          >
            <Text
              style={{
                fontFamily: "archivo-500",
                color: "rgba(255,255,255,1)",
                fontSize: 15,
                textAlign: "center",
              }}
            >
              {product.name}
            </Text>
          </ImageBackground>
        ) : (
          <Text
            style={{
              fontFamily: "archivo-500",
              color: "rgba(255,255,255,1)",
              fontSize: 15,
              textAlign: "center",
            }}
          >
            {product.name}
          </Text>
        )}
      </TouchableOpacity>
      {showProductScreen && (
        <Modal transparent={true}>
          <Animated.View
            style={{
              flex: 1,
              justifyContent: "flex-end",
              alignItems: "flex-start",
              position: "absolute",
              height: "100%",
              width: "100%",
              bottom: 0,
              left: xPos,
              zIndex: 0,
            }}
          >
            <View
              style={{
                height: "100%",
                width: "70%",
                borderTopRightRadius: 3,
              }}
            >
              <ProductListing product={product} goBack={() => fadeOut()} />
            </View>
          </Animated.View>
          <Animated.View
            style={{
              height: "100%",
              width: "30%",
              padding: 20,
              shadowColor: "rgba(0,0,0,1)",
              shadowOffset: {
                width: -3,
                height: 3,
              },
              elevation: 30,
              shadowOpacity: 0.5,
              shadowRadius: 5,
              position: "absolute",
              opacity: shadowOpacity,
              right: 0,
              bottom: 0,
            }}
          />
        </Modal>
      )}
    </>
  );
};

export default ProductDisplayBtn;

const styles = StyleSheet.create({
  touchable: {
    margin: 15,
    width: 180,
    height: 180,
    backgroundColor: "rgba(125,126,132,1)",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 3,
  },
});
