import React, { Component, useEffect, useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  TextInput,
  Modal,
} from "react-native";
import Icon from "react-native-vector-icons/Entypo";

function MultipleTimeSelectableOptionGroup({
  style,
  setopenDropdown,
  openDropdown,
  id,
  label,
  isRequired,
  myObjProfile,
  setmyObjProfile,
  index,
  e,
}) {
  const options = e.optionsList;
  const [localMyObjProfile, setlocalMyObjProfile] = useState(myObjProfile);
  const [optionsSelectedLabel, setoptionsSelectedLabel] = useState("");

  const onMinusPress = ({ option, listIndex }) => {
    const newMyObjProfile = structuredClone(localMyObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs
    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    if (
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes > 0
    ) {
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes -=
        1 * thisItemCountsAs;
    }

    setlocalMyObjProfile(newMyObjProfile);
  };

  const onPlusPress = ({ option, listIndex }) => {
    const newMyObjProfile = structuredClone(localMyObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs

    const selectedItems = newMyObjProfile.options[index].optionsList.filter(
      (op) => op.selectedTimes > 0
    );

    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    let selectedTimesTotal = thisItemCountsAs;

    selectedItems.map((op) => {
      selectedTimesTotal += op.countsAs
        ? parseInt(op.selectedTimes) * parseInt(op.countsAs)
        : parseInt(op.selectedTimes);
    });

    if (
      parseInt(e.numOfSelectable) >= selectedTimesTotal ||
      !e.numOfSelectable ||
      parseInt(e.numOfSelectable) === 0
    ) {
      if (newMyObjProfile.options[index].optionsList[listIndex].selectedTimes) {
        newMyObjProfile.options[index].optionsList[
          listIndex
        ].selectedTimes += 1;
      } else {
        newMyObjProfile.options[index].optionsList[listIndex].selectedTimes = 1;
      }
      setlocalMyObjProfile(newMyObjProfile);
    } else {
      console.log(
        "Didnt Work ",
        "selectedTimesTotal: ",
        selectedTimesTotal,
        " e.numOfSelectable: ",
        e.numOfSelectable
      );
    }
  };

  const dropdownRef = useRef(); // Reference to the original button
  const [dropdownLayout, setDropdownLayout] = useState();

  useEffect(() => {
    dropdownRef.current.measureInWindow((x, y, width, height) => {
      setDropdownLayout({ x, y, width, height });
    });
  }, []);

  const handleKeyDown = (e, option, listIndex) => {
    // Prevent manual editing
    e.preventDefault();

    if (e.key === "ArrowUp") {
      onPlusPress({ option, listIndex });
    } else if (e.key === "ArrowDown") {
      onMinusPress({ option, listIndex });
    }
  };

  useEffect(() => {
    const optionsSelected = localMyObjProfile.options[index].optionsList.filter(
      (op) => op.selectedTimes > 0
    );
    setoptionsSelectedLabel(
      optionsSelected.length > 0
        ? optionsSelected.map((op, index) => {
            if (index > 0) return `, ${op.selectedTimes} X ${op.label}`;
            return `${op.selectedTimes} X ${op.label}`;
          })
        : ""
    );
  }, [localMyObjProfile]);

  return (
    <View
      style={[styles.container, style, openDropdown === id && { zIndex: 1000 }]}
    >
      <Text style={styles.lbl}>
        {label} {isRequired ? "*" : ""}
      </Text>
      <View ref={dropdownRef} style={{ width: "70%" }}>
        <TouchableOpacity
          style={styles.dropdown}
          onPress={() => {
            console.log("openDropdown", openDropdown, id);
            if (openDropdown === id) {
              setopenDropdown(null);
              setmyObjProfile(localMyObjProfile); // Save the local changes
            } else {
              setopenDropdown(id);
            }
          }}
        >
          {optionsSelectedLabel !== "" ? (
            <Text style={styles.placeholder}>
              Selected{" "}
              {optionsSelectedLabel}
            </Text>
          ) : (
            <Text style={styles.placeholder}>Select {label}</Text>
          )}
          <Icon
            name={
              openDropdown === id ? "chevron-small-up" : "chevron-small-down"
            }
            style={styles.downIcon}
          ></Icon>
        </TouchableOpacity>
        {openDropdown === id && (
          <ScrollView
            scrollEventThrottle={16} // Adjust as needed for performance
            style={{
              width: "100%",
              position: "absolute",
              backgroundColor: "white",
              bottom: options.length > 3 ? -44 * 3 : -44 * options.length,
              height: options.length > 3 ? 44 * 3 : 44 * options.length,
            }}
          >
            {options.map((option, listIndex) => (
              <View
                key={listIndex}
                id={option.id}
                style={{
                  width: "100%",
                  height: 44,
                  backgroundColor: "white",
                  padding: 10,
                  borderWidth: 1,
                  justifyContent: "space-between",
                  alignItems: "center",
                  flexDirection: "row",
                }}
              >
                <TouchableOpacity
                  onPress={() => onMinusPress({ option, listIndex })}
                >
                  <Text>-</Text>
                </TouchableOpacity>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "space-between",
                    width: "80%",
                  }}
                >
                  <Text>
                    {`${option.label} ${
                      option.priceIncrease ? `(+$${option.priceIncrease})` : ""
                    }`}
                  </Text>
                  <input
                    style={{
                      border: "none",
                      textAlign: "center",
                      justifyContent: "center",
                      alignItems: "center",
                      width: 40,
                      height: 30,
                      borderWidth: 1,
                      borderColor: "black",
                    }}
                    type="number"
                    value={
                      localMyObjProfile.options[index].optionsList[listIndex]
                        .selectedTimes > 0
                        ? localMyObjProfile.options[index].optionsList[
                            listIndex
                          ].selectedTimes
                        : 0
                    }
                    onKeyDown={(e) => handleKeyDown(e, option, listIndex)}
                    onChange={() => {}}
                    readOnly
                  />
                </View>
                <TouchableOpacity
                  onPress={() => onPlusPress({ option, listIndex })}
                >
                  <Text>+</Text>
                </TouchableOpacity>
              </View>
            ))}
          </ScrollView>
        )}
      </View>
      <Modal visible={openDropdown === id} transparent={true}>
        <TouchableOpacity
          style={{
            width: "100%",
            height: "100%",
          }}
          onPress={() => {
            setopenDropdown(null);
            setmyObjProfile(localMyObjProfile); // Save the local changes
          }} // Hide modal when the background is pressed
        />
        {dropdownLayout && (
          <View
            style={{
              position: "absolute",
              top: dropdownLayout.y,
              left: dropdownLayout.x,
              width: dropdownLayout.width,
            }}
          >
            <TouchableOpacity
              style={styles.dropdown}
              onPress={() => {
                console.log("openDropdown", openDropdown, id);
                if (openDropdown === id) {
                  setopenDropdown(null);
                  setmyObjProfile(localMyObjProfile); // Save the local changes
                } else {
                  setopenDropdown(id);
                }
              }}
            >
              {localMyObjProfile.options.filter((i) => i.selectedTimes > 0)
                .length > 0 ? (
                <Text style={styles.placeholder}>
                  Selected{" "}
                  {localMyObjProfile.options
                    .filter((i) => i.selectedTimes > 0)
                    .map((op, index) => {
                      if (index > 0)
                        return `, ${op.selectedTimes} X ${op.label}`;
                      return `${op.selectedTimes} X ${op.label}`;
                    })}
                </Text>
              ) : (
                <Text style={styles.placeholder}>Select {label}</Text>
              )}
              <Icon
                name={
                  openDropdown === id
                    ? "chevron-small-up"
                    : "chevron-small-down"
                }
                style={styles.downIcon}
              ></Icon>
            </TouchableOpacity>
            {openDropdown === id && (
              <ScrollView
                scrollEventThrottle={16} // Adjust as needed for performance
                style={{
                  width: "100%",
                  position: "absolute",
                  backgroundColor: "white",
                  bottom: options.length > 3 ? -44 * 3 : -44 * options.length,
                  height: options.length > 3 ? 44 * 3 : 44 * options.length,
                }}
              >
                {options.map((option, listIndex) => (
                  <View
                    key={listIndex}
                    id={option.id}
                    style={{
                      width: "100%",
                      height: 44,
                      backgroundColor: "white",
                      padding: 10,
                      borderWidth: 1,
                      justifyContent: "space-between",
                      alignItems: "center",
                      flexDirection: "row",
                    }}
                  >
                    <TouchableOpacity
                      onPress={() => onMinusPress({ option, listIndex })}
                    >
                      <Text>-</Text>
                    </TouchableOpacity>
                    <View
                      style={{
                        flexDirection: "row",
                        alignItems: "center",
                        justifyContent: "space-between",
                        width: "80%",
                      }}
                    >
                      <Text>
                        {`${option.label} ${
                          option.priceIncrease
                            ? `(+$${option.priceIncrease})`
                            : ""
                        }`}
                      </Text>
                      <input
                        style={{
                          border: "none",
                          textAlign: "center",
                          justifyContent: "center",
                          alignItems: "center",
                          width: 40,
                          height: 30,
                          borderWidth: 1,
                          borderColor: "black",
                        }}
                        type="number"
                        value={
                          localMyObjProfile.options[index].optionsList[
                            listIndex
                          ].selectedTimes > 0
                            ? localMyObjProfile.options[index].optionsList[
                                listIndex
                              ].selectedTimes
                            : 0
                        }
                        onKeyDown={(e) => handleKeyDown(e, option, listIndex)}
                        onChange={() => {}}
                        readOnly
                      />
                    </View>
                    <TouchableOpacity
                      onPress={() => onPlusPress({ option, listIndex })}
                    >
                      <Text>+</Text>
                    </TouchableOpacity>
                  </View>
                ))}
              </ScrollView>
            )}
          </View>
        )}
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  lbl: {
    fontWeight: "700",
    color: "#3e3f41",
    width: "25%",
  },
  dropdown: {
    width: "100%",
    height: 44,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
    borderWidth: 2,
    borderColor: "#6987d3",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "flex-start",
  },
  placeholder: {
    fontWeight: "500",
    color: "#7f838c",
    fontSize: 12,
    margin: 10,
  },
  downIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    margin: 0,
    marginTop: 2,
    marginRight: 2,
  },
});

export default MultipleTimeSelectableOptionGroup;
