import {
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useState } from "react";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import useWindowDimensions from "components/functional/useWindowDimensions";
import { addCartState, setCartState } from "state/state";
import PercentageBtn from "./components/PercentageBtn";

const DiscountModal = ({
  setdiscountModal,
  cartSub,
  cart,
  deliveryChecked,
  storeDetails,
}) => {
  const { height, width } = useWindowDimensions();
  const [discountAmount, setdiscountAmount] = useState("");
  const [code, setcode] = useState("");

  return (
    <TouchableOpacity
      onPress={() => setsettingsPasswordModalVis(false)}
      style={{
        justifyContent: "center",
        alignItems: "center",
        height: height,
        width: width,
      }}
      activeOpacity={1}
    >
      <TouchableWithoutFeedback>
        <div style={{ cursor: "default" }}>
    <View style={styles.container}>
      <View style={styles.innerContentContainer}>
        <View style={styles.labelAndInnerGroup}>
          <Text style={styles.containerLbl}>Discount</Text>
          <View style={styles.innerGroup}>
            <Text style={styles.currentTotalLbl}>Current Total: $9.99</Text>
            <View style={styles.inputWithPercentageRow}>
              <View style={styles.customPercentageInput}></View>
              <View style={styles.percentageBtnsRow}>
                <PercentageBtn
                  percentageAmount="5%"
                  style={styles.percentageBtn5}
                  onPress={() => {}}
                  isSelected={true}
                />
                <PercentageBtn
                  percentageAmount="10%"
                  style={styles.percentageBtn10}
                  onPress={() => {}}
                  isSelected={true}
                />
                <PercentageBtn
                  percentageAmount="15%"
                  style={styles.percentageBtn15}
                  onPress={() => {}}
                  isSelected={true}
                />
              </View>
            </View>
            <Text style={styles.newTotalLbl}>New Total: $9.49</Text>
            <View style={styles.managerCodeInput}></View>
          </View>
        </View>
        <View style={styles.confirmAndCancelBtnGroup}>
          <View style={styles.confirmBtn}>
            <Text style={styles.confirmLbl}>Confirm</Text>
          </View>
          <View style={styles.cancelBtn}>
            <Text style={styles.cancelLbl}>Cancel</Text>
          </View>
        </View>
      </View>
          </View>
        </div>
      </TouchableWithoutFeedback>
    </TouchableOpacity>
  );
};

export default DiscountModal;

const styles = StyleSheet.create({
  container: {
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "rgba(255,255,255,1)",
    width: 540,
    height: 439,
  },
  innerContentContainer: {
    width: 439,
    height: 382,
    alignItems: "center",
    justifyContent: "space-between",
  },
  labelAndInnerGroup: {
    width: 439,
    height: 246,
    justifyContent: "space-between",
    alignItems: "center",
  },
  containerLbl: {
    fontFamily: "roboto-700",
    color: "#121212",
    fontSize: 20,
  },
  innerGroup: {
    width: 439,
    height: 202,
    justifyContent: "space-between",
  },
  currentTotalLbl: {
    fontFamily: "roboto-700",
    color: "#121212",
    fontSize: 14,
  },
  inputWithPercentageRow: {
    width: 439,
    height: 52,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  customPercentageInput: {
    width: 259,
    height: 52,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#9e9e9e",
  },
  percentageBtnsRow: {
    width: 156,
    height: 46,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  percentageBtn5: {
    height: 46,
    width: 46,
  },
  percentageBtn10: {
    height: 46,
    width: 46,
  },
  percentageBtn15: {
    height: 46,
    width: 46,
  },
  newTotalLbl: {
    fontFamily: "roboto-700",
    color: "#121212",
    fontSize: 14,
  },
  managerCodeInput: {
    width: 438,
    height: 52,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#9e9e9e",
  },
  confirmAndCancelBtnGroup: {
    width: 284,
    height: 101,
    justifyContent: "space-between",
    alignItems: "center",
  },
  confirmBtn: {
    width: 284,
    height: 42,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  confirmLbl: {
    fontFamily: "roboto-700",
    color: "rgba(255,255,255,1)",
    fontSize: 18,
  },
  cancelBtn: {
    width: 284,
    height: 42,
    backgroundColor: "#edf1fe",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  cancelLbl: {
    fontFamily: "roboto-700",
    color: "rgba(0,0,0,1)",
    fontSize: 18,
  },
});
