import {
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  useWindowDimensions,
  View,
} from "react-native";
import React, { useState, useEffect } from "react";
import { Button, TextInput } from "@react-native-material/core";
import { cartState, setCartState } from "state/state";
import { Switch } from "react-native-gesture-handler";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import Entypo from "@expo/vector-icons/Entypo";
import Ionicons from "@expo/vector-icons/Ionicons";
import Feather from "@expo/vector-icons/Feather";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import { addCustomerDetailsToDb } from "state/firebaseFunctions";
import { auth, db } from "state/firebaseConfig";
import CartItem from "components/CartItem";

const GOOGLE_API_KEY = "AIzaSyDjx4LBIEDNRYKEt-0_TJ6jUcst4a2YON4";

const SaveCustomer = ({
  setSaveCustomerModal,
  setOngoingDelivery,
  setNameForDelivery,
  setPhoneForDelivery,
  setAddressForDelivery,
  setDeliveryChecked,
  setsavedCustomerDetails,
}) => {
  const [customers, setcustomers] = useState([]);
  const [customerSelected, setcustomerSelected] = useState(null);
  const { height, width } = useWindowDimensions();
  const [search, setsearch] = useState(null);

  useEffect(() => {
    db.collection("users")
      .doc(auth.currentUser?.uid)
      .collection("customers")
      .get()
      .then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          // doc.data() is never undefined for query doc snapshots
          //   console.log(doc.id, " => ", doc.data());
          setcustomers((prevState) => [
            ...prevState,
            { ...doc.data(), id: doc.id },
          ]);
        });
      });
  }, []);

  return (
    <>
      <TouchableOpacity
        onPress={() => setSaveCustomerModal(false)}
        style={{
          backgroundColor: "rgba(0, 0, 0, 0.3)",
          justifyContent: "center",
          alignItems: "center",
          height: height,
          width: width,
        }}
      />
      <View
        style={{
          position: "absolute",
          backgroundColor: "rgba(255,255,255,1)",
          borderRadius: 30,
          shadowColor: "rgba(0,0,0,1)",
          shadowOffset: {
            width: 3,
            height: 3,
          },
          elevation: 30,
          shadowOpacity: 0.57,
          shadowRadius: 10,
          width: "35%",
          height: "80%",
          padding: 20,
          alignSelf: "center",
          top: "10%",
        }}
      >
        <View
          style={{
            height: "100%",
            justifyContent: "space-between",
          }}
        >
          <View
            style={[
              {
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "center",
                width: "100%",
              },
              customerSelected
                ? { justifyContent: "space-between" }
                : { justifyContent: "center" },
            ]}
          >
            {customerSelected && (
              <TouchableOpacity onPress={() => setcustomerSelected(null)}>
                <MaterialCommunityIcons
                  name="chevron-left"
                  style={styles.personIcon}
                />
              </TouchableOpacity>
            )}
            <Text
              style={{
                margin: 25,
                fontSize: 20,
                fontWeight: "600",
                textAlign: "center",
              }}
            >
              {customerSelected
                ? `${customerSelected.name}'s Orders`
                : "Saved Customers"}
            </Text>
            {customerSelected && (
              <TouchableOpacity
                onPress={() => {
                  setsavedCustomerDetails(customerSelected);
                  setOngoingDelivery(true);
                  setNameForDelivery(customerSelected.name);
                  setPhoneForDelivery(customerSelected.phone);
                  setAddressForDelivery(customerSelected.address);
                  setDeliveryChecked(true);
                  setSaveCustomerModal(false);
                }}
              >
                <MaterialCommunityIcons name="plus" style={styles.personIcon} />
              </TouchableOpacity>
            )}
          </View>
          {customerSelected ? (
            <View>
              <Text>{customerSelected.name}</Text>
              <Text>{customerSelected.phone}</Text>
              <Text>{customerSelected.address}</Text>
            </View>
          ) : (
            <TextInput
              placeholder="Enter any of customer details"
              value={search}
              onChangeText={(val) => setsearch(val)}
              style={{ width: "80%", alignSelf: "center" }}
            />
          )}
          <ScrollView style={{ height: "80%", minHeight: 300 }}>
            {customerSelected ? (
              <View>
                {customerSelected.orders &&
                  JSON.parse(customerSelected.orders).map(
                    (prevOrder, prevOrderIndex) => (
                      <PrevOrderItem
                        prevOrder={prevOrder}
                        prevOrderIndex={prevOrderIndex}
                      />
                    )
                  )}
              </View>
            ) : (
              customers.map((customer) => {
                if (
                  search?.length > 0 &&
                  !customer.name?.includes(search) &&
                  !customer.phone?.includes(search) &&
                  !customer.address?.includes(search)
                )
                  return;
                return (
                  <TouchableOpacity
                    style={{
                      backgroundColor: "#E6E6E6",
                      flexDirection: "row",
                      alignItems: "center",
                      justifyContent: "space-between",
                      height: 40,
                      marginTop: 15,
                      borderWidth: 1,
                      width: "80%",
                      alignSelf: "center",
                    }}
                    onPress={() => setcustomerSelected(customer)}
                  >
                    <View style={styles.nameGroup}>
                      <MaterialCommunityIcons
                        name="account"
                        style={styles.personIcon}
                      />
                      <Text style={styles.customerNameTxt}>
                        {customer.name}
                      </Text>
                    </View>
                    <Entypo
                      name="chevron-small-right"
                      style={styles.moreInfoIcon}
                    />
                  </TouchableOpacity>
                );
              })
            )}
          </ScrollView>
          <Button
            title={customerSelected ? "Delete Customer" : "Cancel"}
            onPress={() => {
              if (customerSelected) {
                db.collection("users")
                  .doc(auth.currentUser?.uid)
                  .collection("customers")
                  .doc(customerSelected.id)
                  .delete();
                setcustomers((prev) =>
                  prev.filter((e) => e.id !== customerSelected.id)
                );
                setcustomerSelected(null);
              } else {
                setSaveCustomerModal(false);
              }
            }}
            contentContainerStyle={styles.btn}
            style={{ margin: 25, width: "80%", alignSelf: "center" }}
          />
        </View>
      </View>
    </>
  );
};

export default SaveCustomer;

const styles = StyleSheet.create({
  sizeRow: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
