import React, { Component, useState } from "react";
import { StyleSheet, View, Image, TouchableOpacity } from "react-native";
import { Entypo } from "@expo/vector-icons";
import ActiveDropDownOption from "./ActiveDropDownOption";
import NonActiveDropDownOption from "./NonActiveDropDownOption";

function DropDownMenuBtn({ active, label }) {
  const [open, setopen] = useState(false);

  return (
    <View>
      <TouchableOpacity
        style={[
          styles.container,
          active && {
            shadowColor: "rgba(0,0,0,1)",
            shadowOffset: {
              width: 3,
              height: 3,
            },
            elevation: 5,
            shadowOpacity: 0.2,
            shadowRadius: 0,
            borderRadius: 10,
            backgroundColor: "rgba(255,255,255,1)",
          },
        ]}
        onPress={() => setopen((prev) => !prev)}
        activeOpacity={0.8}
      >
        <Image
          source={require("../assets/images/image_ldUi..png")}
          resizeMode="contain"
          style={styles.btnLblImg}
        />
        <Entypo
          name={
            !active
              ? "chevron-small-right"
              : open
              ? "chevron-small-up"
              : "chevron-small-down"
          }
          style={styles.dropDownBtnChevronDown}
        />
      </TouchableOpacity>
      {open && (
        <View style={styles.dropDownOptionsContainer}>
          <ActiveDropDownOption style={styles.activeDropDownOption} />
          <NonActiveDropDownOption style={styles.nonActiveDropDownOption} />
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 20,
    height: 42,
    width: 201,
  },
  btnLblImg: {
    height: 17,
    width: 106,
    marginRight: 10,
    marginLeft: 10,
  },
  dropDownBtnChevronDown: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    marginRight: 10,
    marginLeft: 10,
  },
  dropDownMenuBtn: {
    height: 42,
    width: 201,
  },
  dropDownOptionsContainer: {
    width: 179,
    height: 75,
    justifyContent: "space-between",
    marginBottom: 20,
    marginLeft: 20,
  },
  activeDropDownOption: {
    height: 34,
    width: 179,
  },
  nonActiveDropDownOption: {
    height: 34,
    width: 179,
  },
});

export default DropDownMenuBtn;
