import {
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  useWindowDimensions,
  View,
} from "react-native";
import React, { useState, useEffect } from "react";
import { Button, TextInput } from "@react-native-material/core";
import { cartState, setCartState } from "state/state";
import { Switch } from "react-native-gesture-handler";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import Entypo from "@expo/vector-icons/Entypo";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import { addCustomerDetailsToDb } from "state/firebaseFunctions";
import { auth, db } from "state/firebaseConfig";

const GOOGLE_API_KEY = "AIzaSyDjx4LBIEDNRYKEt-0_TJ6jUcst4a2YON4";

const SaveCustomer = ({
  setSaveCustomerModal,
  setOngoingDelivery,
  setNameForDelivery,
  setPhoneForDelivery,
  setAddressForDelivery,
  setDeliveryChecked,
  setsavedCustomerDetails,
}) => {
  const [customers, setcustomers] = useState([]);
  const [customerSelected, setcustomerSelected] = useState(null);
  const { height, width } = useWindowDimensions();

  useEffect(() => {
    db.collection("users")
      .doc(auth.currentUser?.uid)
      .collection("customers")
      .get()
      .then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          // doc.data() is never undefined for query doc snapshots
          //   console.log(doc.id, " => ", doc.data());
          setcustomers((prevState) => [
            ...prevState,
            { ...doc.data(), id: doc.id },
          ]);
        });
      });
  }, []);

  return (
    <>
      <TouchableOpacity
        onPress={() => setSaveCustomerModal(false)}
        style={{
          backgroundColor: "rgba(0, 0, 0, 0.3)",
          justifyContent: "center",
          alignItems: "center",
          height: height,
          width: width,
        }}
      />
      <View
        style={{
          position: "absolute",
          backgroundColor: "rgba(255,255,255,1)",
          borderRadius: 30,
          shadowColor: "rgba(0,0,0,1)",
          shadowOffset: {
            width: 3,
            height: 3,
          },
          elevation: 30,
          shadowOpacity: 0.57,
          shadowRadius: 10,
          width: "35%",
          height: "80%",
          padding: 20,
          alignSelf: "center",
          top: "10%",
        }}
      >
        <View style={{ justifyContent: "center", borderWidth: 2 }}>
          <Text
            style={{
              margin: 25,
              fontSize: 20,
              fontWeight: "600",
              textAlign: "center",
            }}
          >
            Saved Customers
          </Text>
          {customerSelected ? (
            <View>
              {customerSelected.orders &&
                JSON.parse(customerSelected.orders).map(
                  (prevOrder, prevOrderIndex) => (
                    <View>
                      <TouchableOpacity
                        onPress={() => {
                          setCartState(prevOrder);
                          setOngoingDelivery(true);
                          setNameForDelivery(customerSelected.name);
                          setPhoneForDelivery(customerSelected.phone);
                          setAddressForDelivery(customerSelected.address);
                          setDeliveryChecked(false);
                        }}
                      >
                        <Text>Select Prev Order</Text>
                      </TouchableOpacity>
                      <TouchableOpacity
                        onPress={() => {
                          setCartState(prevOrder);
                          setOngoingDelivery(true);
                          setNameForDelivery(customerSelected.name);
                          setPhoneForDelivery(customerSelected.phone);
                          setAddressForDelivery(customerSelected.address);
                          setDeliveryChecked(true);
                        }}
                      >
                        <Text>Select Prev Order For Delivery</Text>
                      </TouchableOpacity>
                      <TouchableOpacity
                        onPress={() => {
                          const localOrders = JSON.parse(
                            customerSelected.orders
                          );
                          localOrders.splice(prevOrderIndex, 1);
                          db.collection("users")
                            .doc(auth.currentUser?.uid)
                            .collection("customers")
                            .doc(customerSelected.id)
                            .update({
                              orders: JSON.stringify(localOrders),
                            });
                          setcustomerSelected((prev) => ({
                            ...prev,
                            orders: JSON.stringify(localOrders),
                          }));
                        }}
                      >
                        <Text>Delete Prev Order</Text>
                      </TouchableOpacity>
                    </View>
                  )
                )}
              <Button
                title="or Make new order"
                onPress={() => {
                  setsavedCustomerDetails(customerSelected);
                  setOngoingDelivery(true);
                  setNameForDelivery(customerSelected.name);
                  setPhoneForDelivery(customerSelected.phone);
                  setAddressForDelivery(customerSelected.address);
                  setDeliveryChecked(true);
                  setSaveCustomerModal(false);
                }}
              />
            </View>
          ) : (
            customers.map((customer) => {
              return (
                <TouchableOpacity
                  style={{
                    backgroundColor: "#E6E6E6",
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "space-between",
                    height: 40,
                    marginTop: 15,
                    borderWidth: 5,
                    width: "80%",
                    alignSelf: "center",
                  }}
                  onPress={() => setcustomerSelected(customer)}
                >
                  <View style={styles.nameGroup}>
                    <MaterialCommunityIcons
                      name="account"
                      style={styles.personIcon}
                    />
                    <Text style={styles.customerNameTxt}>{customer.name}</Text>
                  </View>
                  <Entypo
                    name="chevron-small-right"
                    style={styles.moreInfoIcon}
                  />
                </TouchableOpacity>
              );
            })
          )}
          <Button
            title="Cancel"
            onPress={() => setSaveCustomerModal(false)}
            contentContainerStyle={styles.btn}
            style={{ margin: 25,  }}
          />
        </View>
      </View>
    </>
  );
};

export default SaveCustomer;

const styles = StyleSheet.create({
  sizeRow: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: 25,
  },
  halfRow: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    width: "50%",
  },
  toppingsContainer: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-between",
    marginBottom: 25,
    marginTop: 25,
  },
  touchable: {
    margin: 25,
    width: 300,
  },
  modalContainer: {
    padding: 50,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 30,
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 30,
    shadowOpacity: 0.57,
    shadowRadius: 10,
    width: "35%",
  },
  btn: {
    height: 40,
    justifyContent: "center",
    alignItems: "center",
  },
  input: {
    marginTop: 25,
    marginBottom: 25,
  },
  nameGroup: {
    flexDirection: "row",
    alignItems: "center",
  },
  personIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    marginRight: 10,
  },
  customerNameTxt: {
    fontFamily: "archivo-500",
    color: "#121212",
    fontSize: 15,
  },
  moreInfoIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 40,
  },
});
