import React, { Component, useState } from "react";
import { StyleSheet, View, TouchableOpacity, Text, TextInput } from "react-native";
import FieldInputWithLabel from "./FieldInputWithLabel";

function PickupDetails({
  storeDetails,
  setorderDetails,
  orderDetails,
  setpage,
  width,
}) {
  const [localName, setlocalName] = useState(orderDetails.customer.name);
  const [localPhoneNumber, setlocalPhoneNumber] = useState(
    orderDetails.customer.phone
  );

  const styles = StyleSheet.create({
    fieldsGroup: {
      width: width,
      height: 179,
      justifyContent: "space-between",
    },
    nameField: {
      height: 70,
      width: width,
    },
    addressField: {
      height: 70,
      width: width,
    },
    buzzCodeAndPhoneRow: {
      width: width,
      height: 70,
      flexDirection: "row",
      justifyContent: "space-between",
    },
    buzzCodeField: {
      height: 70,
      width: 175,
    },
    phoneNumberField: {
      height: 70,
      width: 175,
    },
    continueBtn: {
      width: 219,
      height: 60,
      backgroundColor: "rgba(238,125,67,1)",
      borderRadius: 60,
      justifyContent: "center",
      alignItems: "center",
      shadowColor: "rgba(0,0,0,1)",
      shadowOffset: {
        width: 3,
        height: 3,
      },
      elevation: 30,
      shadowOpacity: 0.2,
      shadowRadius: 10,
      marginTop: 20,
    },
    continueBtnTxt: {
      color: "rgba(255,255,255,1)",
      fontSize: 18,
      fontWeight: "700",
    },
  });

  return (
    <>
      <View style={styles.fieldsGroup}>
        {/* <FieldInputWithLabel
          txtInput="Name"
          label="Name*"
          style={styles.nameField}
          value={localName}
          onChangeText={(text) => setlocalName(text)}
        />
        <FieldInputWithLabel
          txtInput="(123) 456-7890"
          label="Phone Number*"
          style={styles.addressField}
          value={localPhoneNumber}
          onChangeText={(text) => setlocalPhoneNumber(text)}
        /> */}
        <TextInput placeholder="Name" value={localName} onChangeText={(text) => setlocalName(text)} />
      </View>
      <TouchableOpacity
        style={styles.continueBtn}
        onPress={() => {
          if (localName === "" || localPhoneNumber === "")
            return alert("Please fill in all fields");
          setorderDetails((prev) => ({
            ...prev,
            customer: {
              ...prev.customer,
              phone: localPhoneNumber,
              name: localName,
            },
          }));
          setpage(2);
        }}
      >
        <Text style={styles.continueBtnTxt}>CONTINUE</Text>
      </TouchableOpacity>
    </>
  );
}

export default PickupDetails;
