import React, { Component } from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import { Entypo, Ionicons, Feather } from "@expo/vector-icons";
import { auth, db } from "state/firebaseConfig";
import { updateTransList } from "state/firebaseFunctions";

function FinishPaymentCash({
  currentOrder,
  updateOrderHandler,
  fadeIn,
  fadeOut,
  setcurrentOrder,
}) {
  const { element, index, type, cartString, date } = currentOrder;

  return (
    <View style={styles.container}>
      <Text style={styles.paymentDetailsLabel}>Payment Details</Text>
      <View style={styles.mainPartGroup}>
        <Text style={styles.orderTotal}>Total: $9.99</Text>
        <View style={styles.amountPaidTxtInput}></View>
        <View style={styles.changeDueRow}>
          <Text style={styles.changeDue}>Change Due:</Text>
          <Text style={styles.changeDueValue}>$3.50</Text>
        </View>
      </View>
      <View style={styles.btnsGroup}>
        <TouchableOpacity style={styles.finishPaymentBtn}>
          <Text style={styles.finishPayment}>Finish Payment</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.payByCardBtn}>
          <Text style={styles.payByCard}>Pay By Card</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.cancelBtn}>
          <Text style={styles.cancel}>Cancel</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "flex-start",
    width: 540,
    height: 609,
  },
  paymentDetailsLabel: {
    fontWeight: '700',
    color: "#121212",
    fontSize: 20,
    marginTop: 55,
  },
  mainPartGroup: {
