import { StyleSheet, Text, View } from 'react-native'
import React from 'react'
import { TouchableOpacity } from 'react-native-gesture-handler';

const DiscountModal = ({setdiscountModal}) => {
  return (
    <>
      <TouchableOpacity
        onPress={() => setdiscountModal(false)}
        style={{
          backgroundColor: "rgba(0, 0, 0, 0.3)",
          justifyContent: "center",
          alignItems: "center",
          height: height,
          width: width,
        }}
      />
      <View
        style={{
          position: "absolute",
          backgroundColor: "rgba(255,255,255,1)",
          borderRadius: 30,
          shadowColor: "rgba(0,0,0,1)",
          shadowOffset: {
            width: 3,
            height: 3,
          },
          elevation: 30,
          shadowOpacity: 0.57,
          shadowRadius: 10,
          height: height * 0.7,
          width: height * 0.7,
          padding: 20,
          alignSelf: "center",
          top: "15%",
        }}
      >
        <View>
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons
              onPress={() => setdiscountModal(false)}
              name="close"
              size={26}
              color="rgba(74,74,74,1)"
              style={{ width: "25%", marginLeft: 20 }}
            />
            <Text
              style={{
                margin: 25,
                fontSize: 20,
                fontWeight: "600",
                width: "50%",
                textAlign: "center",
              }}
            >
              Apply Discount
            </Text>
            <View style={{ width: "25%" }} />
          </View>
        </View>
      </View>
    </>
  );
}

export default DiscountModal

const styles = StyleSheet.create({})
