import {
  StyleSheet,
  TouchableOpacity,
  TouchableWithoutFeedback,
  View,
} from "react-native";
import React, { useState } from "react";
import { Text, TextInput } from "@react-native-material/core";
import {
  setIsSignedInSettingsState,
  storeDetailState,
} from "state/state";
import Feather from "@expo/vector-icons/Feather";
import Ionicons from "@expo/vector-icons/Ionicons";
import useWindowDimensions from "components/functional/useWindowDimensions";
import { useFonts } from "expo-font";
import Axios from "axios";
import { auth } from "state/firebaseConfig";
import { useHistory } from "react-router-dom";

const SettingsPasswordModal = ({ setsettingsPasswordModalVis, navigation }) => {
  const { height, width } = useWindowDimensions();
  const [password, setpassword] = useState("");
  const storeDetails = storeDetailState.use();
  const [inccorectPass, setinccorectPass] = useState(false);
  const [showPassword, setShowPassword] = useState(false); 
  const history = useHistory();

  useFonts({
    Password: require("/assets/password.ttf"),
  });

  const SendEmail = () => {
    const data = JSON.stringify({
      email: auth.currentUser?.email,
      password: storeDetails.settingsPassword,
    });

    const config = {
      method: "post",
      maxBodyLength: Infinity,
      url: "https://us-central1-posmate-5fc0a.cloudfunctions.net/sendSettingsPass",
      headers: {
        "Content-Type": "application/json",
      },
      data: data,
    };

    Axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data));
        alert("Settings password has been sent to your account email");
      })
      .catch(function (error) {
        alert(error);
      });
  };

  return (
    <TouchableOpacity
      onPress={() => setclockinModal(false)}
      style={{
        justifyContent: "center",
        alignItems: "center",
        height: height,
        width: width,
      }}
      activeOpacity={1}
    >
      <TouchableWithoutFeedback>
        <div style={{ cursor: "default" }}>
          <View style={styles.container}>
            <View style={styles.topLabelSectionContainer}>
              <Text style={styles.settingsLabel}>Settings</Text>
              <Text style={styles.authorizationLabel}>Authorization</Text>
            </View>
            <View style={styles.passwordTxtInput}></View>
            <View style={styles.bottomSectionContainer}>
              <View style={styles.goBtn}>
                <Text style={styles.goBtnTxt}>Go</Text>
              </View>
              <Text style={styles.forgotPasswordTxt}>Forgot Password?</Text>
            </View>
          </View>
        </div>
      </TouchableWithoutFeedback>
    </TouchableOpacity>
  );
};

export default SettingsPasswordModal;

const styles = StyleSheet.create({
  contentContainer: {
    justifyContent: "space-between",
    paddingLeft: 50,
    paddingRight: 50,
    paddingBottom: 50,
    height: "95%",
    width: "100%",
    alignItems: "center",
  },
});
