import React, { Component, useState } from "react";
import { FontAwesome, Entypo } from "@expo/vector-icons";
import DeliveryDetails from "./components/home/DeliveryDetails";
import PickupDetails from "./components/home/PickupDetails";
import {
  Text,
  TouchableOpacity,
  View,
  useWindowDimensions,
  Image,
  StyleSheet,
} from "react-native";
import CheckOutDetails from "./components/home/CheckOutDetails";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";

function OnlineOrderHomeCheckout({
  storeDetails,
  setorderDetails,
  orderDetails,
  setpage,
  page,
  enterDetailsSection,
  setenterDetailsSection,
}) {
  const screenWidth = useWindowDimensions().width;
  const screenHeight = useWindowDimensions().height;

  return (
    <>
      {screenWidth > 1000 ? (
        <Elements stripe={loadStripe(storeDetails.stripePublicKey)}>
          <CheckOutDetails
            storeDetails={storeDetails}
            setorderDetails={setorderDetails}
            orderDetails={orderDetails}
            setpage={setpage}
          />
        </Elements>
      ) : (
        <View
          style={{
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
          }}
        >
          <Elements stripe={loadStripe(storeDetails.stripePublicKey)}>
            <CheckOutDetails
              storeDetails={storeDetails}
              setorderDetails={setorderDetails}
              orderDetails={orderDetails}
              setpage={setpage}
            />
          </Elements>
        </View>
      )}
    </>
  );
}

