import React, { Component } from "react";
import { StyleSheet, View, TouchableOpacity, Text } from "react-native";
import FieldInputWithLabel from "./FieldInputWithLabel";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
const GOOGLE_API_KEY = "AIzaSyDjx4LBIEDNRYKEt-0_TJ6jUcst4a2YON4";

function DeliveryDetails({
  storeDetails,
  setorderDetails,
  orderDetails,
  setpage,
}) {
  return (
    <>
      <View style={styles.fieldsGroup}>
        <FieldInputWithLabel
          txtInput="Name"
          label="Name*"
          style={styles.nameField}
          value={orderDetails.customer.name}
          onChangeText={(text) =>
            setorderDetails({
              ...orderDetails,
              customer: { ...orderDetails.customer, name: text },
            })
          }
        ></FieldInputWithLabel>
        <FieldInputWithLabel
          txtInput="Delivery Address"
          label="Delivery Address*"
          style={styles.addressField}
          customInput={
            <GooglePlacesAutocomplete
              apiOptions={{
                region: "CA",
              }}
              debounce={800}
              apiKey={GOOGLE_API_KEY}
              // onSelect={handleAddress}
              selectProps={{
                address: orderDetails.address,
                onChange: (address) =>
                  setorderDetails({ ...orderDetails, address }),
                defaultValue: orderDetails.address,
                menuPortalTarget: document.body,
                styles: {
                  menuPortal: (base) => ({ ...base, zIndex: 9999 }),
                },
              }}
              renderSuggestions={(active, suggestions, onSelectSuggestion) => (
                <div>
                  {suggestions.map((suggestion) => (
                    <div
                      className="suggestion"
                      onClick={(event) => {
                        onSelectSuggestion(suggestion, event);
                      }}
                    >
                      {suggestion.description}
                    </div>
                  ))}
                </div>
              )}
            />
          }
        ></FieldInputWithLabel>
        <View style={styles.buzzCodeAndPhoneRow}>
          <FieldInputWithLabel
            txtInput="#"
            label="Buzz Code"
            style={styles.buzzCodeField}
            value={orderDetails.customer.buzzCode}
            onChangeText={(text) =>
              setorderDetails({
                ...orderDetails,
                customer: { ...orderDetails.customer, buzzCode: text },
              })
            }
          ></FieldInputWithLabel>
          <FieldInputWithLabel
            txtInput="(123) 456-7890"
            label="Phone Number*"
            style={styles.phoneNumberField}
            value={orderDetails.customer.phone}
            onChangeText={(text) =>
              setorderDetails({
                ...orderDetails,
                customer: { ...orderDetails.customer, phone: text },
              })
            }
          ></FieldInputWithLabel>
        </View>
      </View>
      <TouchableOpacity
        style={styles.continueBtn}
        onPress={() => {
          if (
            orderDetails.customer.name === "" ||
            orderDetails.customer.phone === ""
          )
            return alert("Please fill in all fields");
          setpage(2);
        }}
      >
        <Text style={styles.continueBtnTxt}>CONTINUE</Text>
      </TouchableOpacity>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    width: 380,
    height: 390,
  },
  fieldsGroup: {
    width: 380,
    height: 270,
    justifyContent: "space-between",
  },
  nameField: {
    height: 70,
    width: 380,
  },
  addressField: {
    height: 70,
    width: 380,
  },
  buzzCodeAndPhoneRow: {
    width: 380,
    height: 70,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  buzzCodeField: {
    height: 70,
    width: 175,
  },
  phoneNumberField: {
    height: 70,
    width: 175,
  },
  continueBtn: {
    width: 219,
    height: 60,
    backgroundColor: "rgba(238,125,67,1)",
    borderRadius: 60,
    justifyContent: "center",
    alignItems: "center",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 30,
    shadowOpacity: 0.2,
    shadowRadius: 10,
  },
  continueBtnTxt: {
    color: "rgba(255,255,255,1)",
    fontSize: 18,
    fontWeight: "700",
  },
});

export default DeliveryDetails;
