import React, { useEffect, useState } from "react";
import { employeesState, myDeviceDetailsState, storeDetailState, woocommerceState } from "state/state";
import { auth, db } from "state/firebaseConfig";
const tz = require("moment-timezone");
import { Button, Modal, Text, TouchableOpacity, View } from "react-native";
import tw from 'twrnc';
import { useHistory } from "react-router-dom";

const EmployeesReport = () => {
    const employees = employeesState.use()
    const history = useHistory();

    return (
        <View style={styles.container}>
            <View style={styles.topRow}>
                <Text style={styles.employeeReportTopHeaderTxt}>Employee Report</Text>
                <TouchableOpacity style={styles.addEmployeeBtn}>
                    <Text style={styles.addEmployeeBtnLbl}>Add Employee</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.employeeMapContainer}>
                <View style={styles.innerMapContainer}>
                    <View style={styles.employeeNameTopRowHeader}>
                        <Text style={styles.employeeNameHeader}>Employee Name</Text>
                    </View>
                    <View style={styles.employeeMap}>
                        <ScrollView
                            horizontal={false}
                            contentContainerStyle={styles.employeeMap_contentContainerStyle}
                        >
                            <EmployeeItem style={styles.employeeItem}></EmployeeItem>
                            <EmployeeItem style={styles.employeeItem1}></EmployeeItem>
                            <EmployeeItem style={styles.employeeItem2}></EmployeeItem>
                            <EmployeeItem style={styles.employeeItem3}></EmployeeItem>
                        </ScrollView>
                    </View>
                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        width: 1055,
        height: 506
    },
    topRow: {
        width: 1055,
        height: 48,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    employeeReportTopHeaderTxt: {
        fontFamily: "roboto-700",
        color: "#121212",
        fontSize: 16
    },
    addEmployeeBtn: {
