    employeeMapContainer: {
        width: 1055,
        height: 417,
        borderWidth: 1,
        borderColor: "#bdbfc9",
        shadowColor: "#c5c7d1",
        shadowOffset: {
            width: 3,
            height: 3
        },
        elevation: 60,
        shadowOpacity: 1,
        shadowRadius: 20,
        alignItems: "center",
        marginTop: 41
    },
    innerMapContainer: {
        width: 1002,
        height: 301,
        justifyContent: "flex-start",
        marginTop: 32
    },
    employeeNameTopRowHeader: {
        width: 1000,
        height: 36,
        borderWidth: 0,
        borderColor: "#cbcdda",
        borderBottomWidth: 1,
        justifyContent: "flex-start"
    },
    employeeNameHeader: {
        fontFamily: "roboto-700",
        color: "#61656f"
    },
    employeeMap: {
        width: 1002,
        height: 266
    },
    employeeMap_contentContainerStyle: {
        height: 266,
