import React, { useEffect, useRef, useState } from "react";
import {
    View,
    Text,
    ScrollView,
    StyleSheet,
    Animated,
    Modal,
    TouchableOpacity,
    Dimensions
} from "react-native";
import { Button, Switch, TextInput } from "@react-native-material/core";
import {
    deviceIdState,
    deviceTreeState,
    setDeviceTreeState,
    setMyDeviceDetailsState,
} from "state/state";
import { auth, db } from "state/firebaseConfig";
import ReactSelect from "react-select";
import { loadStripe } from "@stripe/stripe-js";
import { Image } from "react-native";
import GeneralSwitch from "components/GeneralSwitch";
import { Entypo, Ionicons } from "@expo/vector-icons";

function Index(props) {
    const deviceTree = deviceTreeState.use()
    const myDeviceID = deviceIdState.use()
    const fadeAnim = useRef(new Animated.Value(0)).current;
    const [viewVisible, setviewVisible] = useState(false);
    const { width, height } = Dimensions.get('window');
    const [selectedDevice, setselectedDevice] = useState(0)

    const fadeIn = () => {
        // Will change fadeAnim value to 0 in 3 seconds
        Animated.timing(fadeAnim, {
            toValue: 1,
            duration: 500,
            useNativeDriver: false,
        }).start();
    };

    const resetLoader = () => {
        setviewVisible(true);
        fadeIn();
    };

    const AddNewDevice = async () => {
        await db
            .collection("users")
            .doc(auth.currentUser.uid)
            .collection("checkout_sessions")
            .add({
                price: 'price_1Oj9NZCIw3L7DOwI7DKAhIve', // todo price Id from your products price in the Stripe Dashboard
                quantity: 1,
                success_url: window.location.href, // return user to this screen on successful purchase
                cancel_url: window.location.href, // return user to this screen on failed purchase
            })
            .then((docRef) => {
                // Wait for the checkoutSession to get attached by the extension
                docRef.onSnapshot(async (snap) => {
                    const { error, sessionId } = snap.data();
                    if (error) {
                        // Show an error to your customer and inspect
                        // your Cloud Function logs in the Firebase console.
                        alert(`An error occurred: ${error.message}`);
                    }

                    if (sessionId) {
                        // We have a session, let's redirect to Checkout
                        // Init Stripe
                        const stripe = await loadStripe(
                            "pk_live_51MHqrvCIw3L7DOwI0ol9CTCSH7mQXTLKpxTWKzmwOY1MdKwaYwhdJq6WTpkWdBeql3sS44JmybynlRnaO2nSa1FK001dHiEOZO" // todo enter your public stripe key here
                        );
                        console.log(`redirecting`);
                        await stripe.redirectToCheckout({ sessionId });
                    }
                });
            });
    }

    return (
        <View style={styles.container}>
            <Text style={styles.pageLbl}>Device Settings</Text>
            <View style={styles.group}>
                <View style={styles.deviceScrollContainer}>
                    <View style={styles.backBtn}></View>
                    {deviceTree.devices.length > 0 ? <View style={styles.deviceContainer}>
                        <View style={styles.topGroup}>
                            <View style={styles.deviceNameInputGroup}>
                                <Text style={styles.deviceName}>Device Name</Text>
                                <TextInput style={styles.deviceNameInput} placeholder="Enter device name" />
                            </View>
                            <View style={styles.deviceIDRow}>
                                <Text style={styles.deviceIdLbl}>Device ID:</Text>
                                <Text style={styles.deviceId}>21932HNQDH32H1H321H</Text>
                                <TouchableOpacity style={styles.setToMyIDBtn} activeOpacity={0.8}>
                                    <Ionicons
                                        name="md-key"
                                        style={styles.setToMyIDIcon}
                                    />
                                </TouchableOpacity>
                            </View>
                            <View style={styles.printOnlineOrderRow}>
                                <Text style={styles.printOnlineOrdersLbl}>
                                    Print Online Orders:
                                </Text>
                                <GeneralSwitch />
                            </View>
                            <View style={styles.useDifferentDeviceRow}>
                                <Text style={styles.useDifferentDeviceLbl}>
                                    Use Different Device To Print:
                                </Text>
                                <GeneralSwitch />
                            </View>
                            <View style={styles.printerToPrinterInputGroup}>
                                <Text style={styles.printToPrinterLbl}>Print to Printer</Text>
                                <TextInput style={styles.printToPrintInput} placeholder="Enter printer name" />
                            </View>
                        </View>
                        <View style={styles.btnsRow}>
                            <TouchableOpacity style={styles.updateDeviceBtn} activeOpacity={0.8}>
                                <Text style={styles.saveDevice}>Save Device</Text>
                            </TouchableOpacity>
                            <TouchableOpacity style={styles.deleteDeviceBtn} activeOpacity={0.8}>
                                <Text style={styles.deleteDevice}>Delete Device</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                        :
                        <View>
                            <Text>No Devices Found</Text>
                        </View>
                    }
                    <TouchableOpacity style={styles.nextDeviceBtn} activeOpacity={0.8}>
                        <Entypo name="chevron-right" style={styles.icon} />
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        alignItems: "flex-start",
        justifyContent: "flex-start",
        width: '100%',
        height: '100%'
    },
    pageLbl: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 16,
        margin: 20
    },
    group: {
        alignSelf: "stretch",
        flex: 1,
        justifyContent: "center",
        alignItems: "center"
    },
    deviceScrollContainer: {
        width: 639,
        // height: 591,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-between",
        margin: 20
    },
    backBtn: {
        width: 50,
        height: 50,
        borderRadius: 20,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center"
    },
    deviceContainer: {
        width: 358,
        height: 468,
        justifyContent: "space-between",
        alignItems: "center"
    },
    topGroup: {
        width: 358,
        height: 360,
        justifyContent: "space-between"
    },
    deviceNameInputGroup: {
        width: 358,
        height: 88,
        justifyContent: "space-between"
    },
    deviceName: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 17
    },
    deviceNameInput: {
        width: 358,
        height: 51,
        backgroundColor: "#ffffff",
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#a0a0a0",
        padding: 10
    },
    deviceIDRow: {
        width: 354,
        height: 20,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-between"
    },
    deviceIdLbl: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 17
    },
    deviceId: {
        fontWeight: '300',
        color: "#121212",
        fontSize: 17
    },
    setToMyIDBtn: {
        width: 30,
        height: 30,
        backgroundColor: "#1c294e",
        borderRadius: 10,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center"
    },
    setToMyIDIcon: {
        color: "rgba(255,255,255,1)",
        fontSize: 25
    },
    printOnlineOrderRow: {
        width: 356,
        height: 21,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    printOnlineOrdersLbl: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 17
    },
    printOnlineSwitch: {
        width: 40,
        height: 20,
        backgroundColor: "#E6E6E6"
    },
    useDifferentDeviceRow: {
        width: 356,
        height: 20,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    useDifferentDeviceLbl: {
        fontWeight: '700',
        color: "#111111",
        fontSize: 17
    },
    useDiffrentDeviceSwitch: {
        width: 40,
        height: 20,
        backgroundColor: "#E6E6E6"
    },
    printerToPrinterInputGroup: {
        width: 358,
        height: 88,
        justifyContent: "space-between"
    },
    printToPrinterLbl: {
        fontWeight: '700',
        color: "#111111",
        fontSize: 17
    },
    printToPrintInput: {
        width: 358,
        height: 51,
        backgroundColor: "#ffffff",
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#a0a0a0",
        padding: 10
    },
    btnsRow: {
        width: 356,
        height: 49,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    updateDeviceBtn: {
        width: 170,
        height: 48,
        backgroundColor: "rgba(76,175,80,1)",
        borderRadius: 20,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center"
    },
    saveDevice: {
        fontWeight: '700',
        color: "rgba(255,255,255,1)",
        fontSize: 16
    },
    deleteDeviceBtn: {
        width: 170,
        height: 48,
        backgroundColor: "rgba(244,67,54,1)",
        borderRadius: 20,
        opacity: 0.61,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center"
    },
    deleteDevice: {
        fontWeight: '700',
        color: "rgba(255,255,255,1)",
        fontSize: 16
    },
    nextDeviceBtn: {
        width: 50,
        height: 50,
        backgroundColor: "#1c294e",
        borderRadius: 20,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center"
    },
    icon: {
        color: "rgba(255,255,255,1)",
        fontSize: 40
    }
});

export default Index;
