import React, { useState } from "react";

type GeneralSwitchProps = {
  isActive: boolean;
  toggle?: () => void;
};

const GeneralSwitch = ({
  isActive,
  toggle,
}: GeneralSwitchProps) => {
  // Function to toggle switch state
  const toggleSwitch = () => {
      toggle();
  };

  // Styles
  const switchStyles = {
    cursor: "pointer",
    width: "39px",
    height: "18px",
    borderRadius: "15px",
    backgroundColor: isActive ? "#1D294E" : "#ccc",
    position: "relative",
    transition: "background-color 0.3s",
  };

  const knobStyles = {
    position: "absolute",
    top: "0px",
    left: isActive && "21px", // Move knob based on isActive
    width: "19px",
    height: "19px",
    borderRadius: "50%",
    backgroundColor: "#EEF2FF",
    transition: "left 0.3s",
  };

  return (
    <div style={switchStyles} onClick={toggleSwitch}>
      <div style={knobStyles}></div>
    </div>
  );
};

export default GeneralSwitch;
