import ProductImage from "components/ProductImage";
import { AvocatImage } from "pages/authed/backendPos/EntryFile/imagePath";
import React, { Component } from "react";
import { StyleSheet, TouchableOpacity, Image, Text, View } from "react-native";
import Icon from "react-native-vector-icons/Feather";
import { useHistory } from "react-router-dom";

function ProductOptionBox({ style, product, index, editMode }) {
  const history = useHistory();

  return (
    <TouchableOpacity
      style={[styles.container, style]}
      onPress={() =>
        history.push(`/authed/product/editproduct-product/${product.id}`)
      }
      activeOpacity={0.8}
    >
      <View>
        <ProductImage
          source={product.imageUrl ? { uri: product.imageUrl } : AvocatImage}
          resizeMode="contain"
          style={styles.productImage}
        />
      </View>
      <Text style={styles.productNameTxt}>
        {product.name.length > 20
          ? product.name.substring(0, 20) + "..."
          : product.name}
      </Text>
      <Text style={styles.productPriceTxt}>${product.price}</Text>
      <View style={styles.editProductBtn}>
        <Icon name="edit-3" style={styles.editProductIcon}></Icon>
        <Text style={styles.editProductTxt}>Edit Product</Text>
      </View>
      {editMode && (
        <View style={styles.editProductBtn}>
          <Icon name="" style={styles.editProductIcon}></Icon>
          <Text style={styles.editProductTxt}>Edit Product</Text>
        </View>
      )}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    borderRadius: 10,
    backgroundColor: "#f5f5f5",
    alignItems: "center",
    justifyContent: "space-between",
  },
  productImage: {
    height: 125,
    width: 127,
    marginTop: 20,
  },
  productNameTxt: {
    color: "#121212",
    fontSize: 15,
    paddingLeft: 10,
    paddingRight: 10,
  },
  productPriceTxt: {
    fontWeight: "700",
    color: "#20c85c",
    fontSize: 20,
  },
  editProductBtn: {
    width: 215,
    height: 37,
    borderRadius: 10,
    borderBottomRightRadius: 10,
    borderBottomLeftRadius: 10,
    borderTopLeftRadius: 0,
    borderTopRightRadius: 0,
    backgroundColor: "#2b3659",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  editProductIcon: {
    color: "rgba(255,254,254,1)",
    fontSize: 20,
  },
  editProductTxt: {
    color: "rgba(255,255,255,1)",
    fontSize: 14,
    marginLeft: 10,
  },
});

export default ProductOptionBox;
