  Text,
  ScrollView,
  useWindowDimensions,
  StyleSheet,
  Image,
  TouchableOpacity,
  Animated,
  Modal,
} from "react-native";
import React, { useEffect, useRef, useState } from "react";
import { Button, Switch, TextInput } from "@react-native-material/core";
import {
  setStoreDetailState,
  setWoocommerceState,
  storeDetailState,
  woocommerceState,
} from "state/state";
import {
  updateStoreDetails,
  updateWooCredentials,
} from "state/firebaseFunctions";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import Foundation from "@expo/vector-icons/Foundation";
import { auth, db } from "state/firebaseConfig";
import { loadStripe } from "@stripe/stripe-js";
import firebase from "firebase/app";

const HelpModal = () => {
  const { width, height } = useWindowDimensions();
  const [screen, setscreen] = useState("home");

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <View style={styles.detailInputContainer}>
        <View
          style={{
            position: "absolute",
            top: -30,
            left: -1,
            flexDirection: "row",
          }}
        >
          <TouchableOpacity
            onPress={() => setscreen("home")}
            style={{
              width: 120,
              height: 30,
              justifyContent: "center",
              alignItems: "center",
              backgroundColor: screen === "home" ? "#D2D2D2" : "#F5F3F3",
            }}
          >
            <Text>General</Text>
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => setscreen("second")}
            style={{
              width: 120,
              height: 30,
              justifyContent: "center",
              alignItems: "center",
              backgroundColor: screen === "woocommerce" ? "#D2D2D2" : "#F5F3F3",
            }}
          >
            <Text>WooCommerce</Text>
          </TouchableOpacity>
        </View>
        {screen === "home" ? (
          <>
            <View style={{ margin: 10 }}></View>
          </>
        ) : (
          <>
            <View style={{ margin: 10 }}></View>
          </>
        )}
      </View>
    </ScrollView>
  );
};

