import { View, Text, Modal } from "react-native";
import React from "react";

const TutorialStep = (props) => {
  const { children } = props;
  return (
    <>
      <Modal transparent>
        <View
          style={{
            backgroundColor: "rgba(0,0,0,0.5)",
            position: "absolute",
            height: "100%",
            width: "100%",
          }}
        />
      </Modal>
      <View>{children}</View>
    </>
  );
};

export default TutorialStep;
