import React, { Component, useState } from "react";
import { StyleSheet, View, Image } from "react-native";
import { Entypo } from "@expo/vector-icons";

function DropDownMenuBtn(props) {
  const [open, setopen] = useState(false)
  return (
    <View style={styles.openDropDownBtnContainer}>
      <View style={[styles.container, props.style]}>
        <Image
          source={require("../assets/images/image_ldUi..png")}
          resizeMode="contain"
          style={styles.btnLblImg}
        />
        <Entypo
          name="chevron-small-down"
          style={styles.dropDownBtnChevronDown}
        />
      </View>
      {open && <View style={styles.dropDownOptionsContainer}>
        <ActiveDropDownOption style={styles.activeDropDownOption} />
        <NonActiveDropDownOption style={styles.nonActiveDropDownOption} />
      </View>}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 5,
    shadowOpacity: 0.2,
    shadowRadius: 0,
    borderRadius: 10,
    backgroundColor: "rgba(255,255,255,1)",
  },
  btnLblImg: {
    height: 17,
    width: 106,
    marginRight: 10,
    marginLeft: 10,
  },
  dropDownBtnChevronDown: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    marginRight: 10,
    marginLeft: 10,
  },
});

export default DropDownMenuBtn;
