import {
  View,
  Text,
  Button,
  StyleSheet,
  TouchableOpacity,
  TextInput,
} from "react-native";
import React, { useState } from "react";
import Entypo from "@expo/vector-icons/Entypo";
import Feather from "@expo/vector-icons/Feather";
import { userStoreState } from "state/state";

const ProductAndCatManager = ({ navigation }) => {
  const catalog = userStoreState.use();
  const [selectedCategory, setselectedCategory] = useState(null);
  const [selectedProduct, setselectedProduct] = useState(null);

  return (
    <View
      style={{
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        width: "100%",
      }}
    >
      <View
        style={{
          width: "90%",
          height: 60,
          flexDirection: "row",
          marginBottom: 20,
          justifyContent: "flex-start",
          alignItems: "center",
        }}
      >
        <Text
          style={{
            fontFamily: "archivo-600",
            color: "rgba(98,96,96,1)",
            fontSize: 20,
          }}
        >
          Edit Store Catalog
        </Text>
      </View>
      {/* <Button
        title="Edit Products"
        onPress={() => navigation.navigate("Edit ProductList")}
      />
      <Button
        title="Edit Categories"
        onPress={() => navigation.navigate("Edit Categories")}
      /> */}
      <View style={styles.container}>
        <View style={styles.left}>
          <View style={styles.headerContainer}>
            <Text style={styles.headerLbl}>Categories</Text>
          </View>
          <View style={styles.bottomContainer}>
            <View style={styles.addNewContainer}>
              <Entypo name="plus" style={styles.addNewIcon} />
              <Text style={styles.addNewTxt}>Add new category</Text>
            </View>
            {catalog.categories.map((e, index) => {
              return (
                <TouchableOpacity
                  onPress={() => setselectedCategory(e)}
                  style={[
                    styles.selectedItemContainer,
                    e === selectedCategory && { backgroundColor: "#ebebeb" },
                  ]}
                >
                  <Text style={styles.selectedItemTxt}>{e}</Text>
                  <Feather
                    name="more-vertical"
                    style={styles.selectedItemIcon}
                  />
                </TouchableOpacity>
              );
            })}
            <TextInput
              style={[
                styles.selectedItemContainer,
                { borderWidth: 1, borderColor: "#1b73e8", borderRadius: 6 },
              ]}
              placeholder="Enter new category name"
            />
          </View>
        </View>
        <View style={styles.left}>
          <View style={styles.headerContainer}>
            <Text style={styles.headerLbl}>Products</Text>
          </View>
          <View style={styles.bottomContainer}>
            <View style={styles.addNewContainer}>
              <Entypo name="plus" style={styles.addNewIcon} />
              <Text style={styles.addNewTxt}>Add new product</Text>
            </View>
            {catalog.products
              .filter((prod) =>
                prod.category
                  ? prod.category == selectedCategory
                  : prod.catagory == selectedCategory
              )
              .map((e, index) => {
                return (
                  <TouchableOpacity
                    onPress={() => setselectedProduct(e)}
                    style={[
                      styles.selectedItemContainer,
                      e === selectedProduct && { backgroundColor: "#ebebeb" },
                    ]}
                  >
                    <Text style={styles.selectedItemTxt}>{e.name}</Text>
                    <Feather
                      name="more-vertical"
                      style={styles.selectedItemIcon}
                    />
                  </TouchableOpacity>
                );
              })}
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    borderWidth: 1,
    borderColor: "#dcdcdc",
    flexDirection: "row",
    width: "90%",
    height: "80%",
  },
  left: {
    width: "50%",
    height: "100%",
    borderWidth: 1,
    borderColor: "#dcdcdc",
  },
  headerContainer: {
    width: "100%",
    height: 40,
    borderWidth: 0,
    borderColor: "#dcdcdc",
    borderBottomWidth: 1,
    backgroundColor: "#fafafa",
  },
  headerLbl: {
    fontFamily: "archivo-500",
    color: "#717171",
    marginTop: 14,
    marginLeft: 43,
  },
  bottomContainer: {
    width: "100%",
  },
  addNewContainer: {
    width: "100%",
    height: 38,
    flexDirection: "row",
    alignItems: "center",
  },
  addNewIcon: {
    color: "#2076e9",
    fontSize: 30,
    marginLeft: 10,
  },
  addNewTxt: {
    fontFamily: "archivo-500",
    color: "#1b73e8",
    marginLeft: 10,
  },
  selectedItemContainer: {
    height: 33,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    paddingLeft: 40,
    paddingRight: 10,
  },
  selectedItemTxt: {
    fontFamily: "archivo-500",
    color: "#252525",
  },
  selectedItemIcon: {
    color: "#6a6a6a",
    fontSize: 20,
  },
});

export default ProductAndCatManager;
