import React, { Component, useEffect, useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  Modal,
} from "react-native";
import { Entypo } from "@expo/vector-icons";

function GeneralDropdown({ style, placeholder, value, setValue, options }) {
  const dropdownRef = useRef(); // Reference to the original button
  const [dropdownLayout, setDropdownLayout] = useState();
  const [openDropdown, setopenDropdown] = useState(false);

  useEffect(() => {
    dropdownRef.current.measureInWindow((x, y, width, height) => {
      setDropdownLayout({ x, y, width, height });
    });
  }, []);

  return (
    <View style={[styles.container, style, openDropdown && { zIndex: 1000 }]}>
      <View ref={dropdownRef} style={{ width: 188, height: 50 }}>
        <TouchableOpacity
          style={styles.dropdown}
          onPress={() => setopenDropdown((prev) => !prev)}
          activeOpacity={1}
        >
          <Text style={styles.placeholder}>{value ? value : placeholder}</Text>
          <Entypo
            name={openDropdown ? "chevron-small-up" : "chevron-small-down"}
            style={styles.downIcon}
          />
        </TouchableOpacity>
      </View>
      <Modal visible={openDropdown} transparent={true}>
        <TouchableOpacity
          style={{
            width: 188,
            height: 50,
          }}
          onPress={() => {
            setopenDropdown(false);
          }} // Hide modal when the background is pressed
        />
        {dropdownLayout && (
          <View
            style={{
              position: "absolute",
              top: dropdownLayout.y,
              left: dropdownLayout.x,
              width: 188,
            }}
          >
            <TouchableOpacity
              style={styles.dropdown}
              onPress={() => setopenDropdown((prev) => !prev)}
            >
              <Text style={styles.placeholder}>
                {value ? value : placeholder}
              </Text>
              <Entypo
                name={openDropdown ? "chevron-small-up" : "chevron-small-down"}
                style={styles.downIcon}
              />
            </TouchableOpacity>
            {openDropdown && (
              <ScrollView
                style={{
                  width: 188,
                  position: "absolute",
                  backgroundColor: "white",
                  bottom: options.length > 3 ? -50 * 3 : -50 * options.length,
                  height: options.length > 3 ? 50 * 3 : 50 * options.length,
                  borderBottomLeftRadius: 10,
                  borderBottomRightRadius: 10,
                  borderWidth: 1,
                }}
              >
                {options.map((option, listIndex) => (
                  <TouchableOpacity
                    key={listIndex}
                    onPress={() => {
                      setValue(option);
                      setopenDropdown(false);
                    }}
                    style={{
                      width: "100%",
                      height: 50,
                      backgroundColor: "white",
                      padding: 10,
                      borderBottomWidth: 1,
                    }}
                    activeOpacity={1}
                  >
                    <Text>{option}</Text>
                  </TouchableOpacity>
                ))}
              </ScrollView>
            )}
          </View>
        )}
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  lbl: {
    fontWeight: "700",
    color: "#3e3f41",
    width: "25%",
  },
  dropdown: {
    width: 188,
    height: 50,
    backgroundColor: "rgba(255,255,255,1)",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  placeholder: {
    fontWeight: "500",
    color: "#7f838c",
    fontSize: 12,
    margin: 10,
  },
  downIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    margin: 0,
    marginTop: 2,
    marginRight: 2,
  },
});

export default GeneralDropdown;
