import React, { useEffect, useRef, useState } from 'react'
import { Upload } from '../../EntryFile/imagePath';
import Select2 from 'react-select2-wrapper';
import 'react-select2-wrapper/css/select2.css';
import { employeesState, setEmployeesState, setUserStoreState, userState, userStoreState } from 'state/state';
import { updateData } from 'state/firebaseFunctions';
import { FlatList, Image, Modal, TouchableOpacity, View } from 'react-native';
import OptionView from 'components/OptionView';
import { Button } from 'react-native';
import { Link, useHistory, useParams } from 'react-router-dom';
import { auth, db, storage } from 'state/firebaseConfig';

import DatePicker from "react-datepicker";
import { Text } from '@react-native-material/core';

const EditEmployee = () => {
    const { employeeId } = useParams()
    const employees = employeesState.use()
    const [employee, setemployee] = useState(employees[employeeId]);
    const [dateSelected, setdateSelected] = useState(null)
    const [startTime, setstartTime] = useState(null)
    const [endTime, setendTime] = useState(null)
    const [allHours, setallHours] = useState([])

    const history = useHistory();
    const [error, seterror] = useState(false)

    useEffect(() => {
        if (!employees.length > 0) {
            history.push("/authed/report/employeesreport")
            return
        }
        db.collection("users").doc(auth.currentUser.uid).collection("employees").doc(employee.id.toString()).collection("hours").get((snapshot) => {
            console.log('Made it before empty check')
            if (snapshot.empty) return
            const hours = []
            snapshot.forEach((doc) => {
                hours.push(doc.data())
            })
            setallHours(hours)
        }
        )
    }, [])


    function handleDataUpdate() {
        if (!employee.name) {
            seterror('Please enter a employee name')
            return
        }
        db.collection("users").doc(auth.currentUser.uid).collection("employees").doc(employee.id.toString()).update(employee)
        const newEmployeesList = [...employees]
        newEmployeesList[employeeId] = employee
        setEmployeesState(newEmployeesList)
        history.push("/authed/report/employeesreport")
    }

    return (
        <>
            <div className="page-wrapper">
                <div className="content">
                    <div className="page-header">
                        <div className="page-title">
                            <h4>Employee Edit</h4>
                            <h6>Edit your employee details</h6>
                        </div>
                    </div>
                    {/* /add */}
                    <div className="card">
                        <div className="card-body">
                            <div className="row">
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Employee Name</label>
                                        <input type="text" placeholder="Product Name" value={employee?.name} onChange={(event) => setemployee((prevState) => ({
                                            ...prevState,
                                            name: event.target.value,
                                        }))} />
                                    </div>
                                </div>

                                <View style={{ marginBottom: 50 }}>
                                    <Text>Add Hours</Text>
                                    <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', width: '100%', marginTop: 10 }}>
                                        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                                            <Text>Date:</Text>
                                            <DatePicker selected={dateSelected} onSelect={setdateSelected}
                                            />
                                        </View>
                                        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                                            <Text>Start Time:</Text>
                                            <input aria-label="Time" type="time" onChange={(event) =>
                                                setstartTime(event.target.value)
                                            } />
                                        </View>
                                        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                                            <Text>End Time:</Text>
                                            <input aria-label="Time" type="time" onChange={event => setendTime(event.target.value)} />
                                        </View>
                                        <Button title="Add" onPress={() => {
                                            if (!dateSelected || !startTime || !endTime) return
                                            db.collection("users").doc(auth.currentUser.uid).collection("employees").doc(employee.id.toString()).collection("hours").add({
                                                date: dateSelected,
                                                startTime: startTime,
                                                endTime: endTime
                                            })
                                            setallHours([...allHours, {
                                                date: dateSelected,
                                                startTime: startTime,
                                                endTime: endTime
                                            }])
                                            setdateSelected(null)
                                            setstartTime(null)
                                            setendTime(null)
                                            // console.log('HELLO')
                                            // console.log('dateSelected', dateSelected)
                                            // console.log('startTime', startTime)
                                            // console.log('endTime', endTime)
                                        }} />
                                    </View>
                                    {allHours.length > 0 && allHours.map((hour, index) => {
                                        return (
                                            <View key={index} style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between' }}>
                                                <Text>{hour.date.toDateString()}</Text>
                                                <Text>{hour.startTime}</Text>
                                                <Text>{hour.endTime}</Text>
                                            </View>
                                        )
                                    }
                                    )}
                                </View>

                                <div className="col-lg-12">
                                    <button className="btn btn-submit me-2" onClick={handleDataUpdate}>
                                        Update
                                    </button>
                                    <Link style={{ textDecoration: 'none' }} to="/authed/product/productlist-product" className="btn btn-cancel">
                                        Cancel
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </div>
                    <Modal visible={error} transparent={true}>
                        <TouchableOpacity
                            onPress={() => seterror(false)}
                            style={{
                                height: "100%",
                                width: "100%",
                                justifyContent: "center",
                                alignItems: "center",
                                padding: "20%",
                                backgroundColor: "rgba(0,0,0,0.5)",
                            }}
                        >
                            <div
                                data-wf-user-form-error="true"
                                className=" error-message "
                            >
                                <div className="user-form-error-msg">
                                    {error}
                                </div>
                            </div>
                        </TouchableOpacity>
                    </Modal>
                    {/* /add */}
                </div>
            </div>
        </>
    )
}
export default EditEmployee;
