import React, { Component, useEffect, useState } from "react";
import {
  StyleSheet,
  View,
  Image,
  Text,
  TouchableOpacity,
  ScrollView,
  TextInput,
} from "react-native";
import ProductOptionBox from "./components/ProductOptionBox";
import { Feather, MaterialCommunityIcons } from "@expo/vector-icons";
import { onlineStoreState, setStoreDetailState, setUserStoreState, userState, userStoreState } from "state/state";
import { updateData } from "state/firebaseFunctions";
import { auth, db, storage } from "state/firebaseConfig";
import ProductImage from "components/ProductImage";
import { useHistory } from "react-router-dom";
import Swal from "sweetalert2";
import { AvocatImage } from "../../EntryFile/imagePath";

function ProductList(props) {
  const catalog = userStoreState.use()
  const [inputfilter, setInputfilter] = useState(false);
  const [searchFilterValue, setsearchFilterValue] = useState('')
  const [data, setData] = useState([]);
  const [filteredData, setfilteredData] = useState([]);
  const userS = userState.use();
  const onlineStoreDetails = onlineStoreState.use()
  const history = useHistory()
  const [selectedCategory, setselectedCategory] = useState()

  const togglefilter = (value) => {
    setInputfilter(value);
  };
  const confirmText = (props) => {
    console.log("COnfirm Text: ", props);
    Swal.fire({
      title: "Are you sure?",
      text: "You won't be able to revert this!",
      type: "warning",
      showCancelButton: !0,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, delete it!",
      confirmButtonClass: "btn btn-primary",
      cancelButtonClass: "btn btn-danger ml-1",
      buttonsStyling: !1,
    }).then(function (t) {
      if (t.value) {
        Swal.fire({
          type: "success",
          title: "Deleted!",
          text: "Your file has been deleted.",
          confirmButtonClass: "btn btn-success",
        });
        let localCatalog = structuredClone(catalog);
        if (localCatalog.products.length > 1) {
          localCatalog.products = localCatalog.products.filter((item) => item.id !== props.id);
        } else {
          localCatalog.products = [];
        }
        setUserStoreState({ categories: localCatalog.categories, products: localCatalog.products })
        db.collection("users")
          .doc(userS.uid)
          .collection("products")
          .doc(props.id.toString())
          .delete()
        if (onlineStoreDetails.onlineStoreSetUp) {
          db.collection("public")
            .doc(userS.uid)
            .collection("products")
            .doc(props.id.toString())
            .delete()
        }
      }
    });
  };

  useEffect(() => {
    if (catalog.products.length > 0) {
      setData([]);
      catalog.products.map((product, index) => {
        setData((prev) => {

          const productsWithCategory = prev.filter((item) => item.category === product.category)
          if (productsWithCategory.length > 0) {
            const indexOfLastItem = prev.indexOf(productsWithCategory[productsWithCategory.length - 1])
            prev.splice(indexOfLastItem + 1, 0, {
              id: product.id,
              index: index + 1,
              image: product.imageUrl ? product.imageUrl : AvocatImage,
              productName: product.name,
              category: product.catagory ? product.catagory : product.category,
              price: product.price,
              createdBy: "Admin",
              hasImage: product.hasImage
            })
            return prev
          }

          return [
            ...prev,
            {
              id: product.id,
              index: index + 1,
              image: product.imageUrl ? product.imageUrl : AvocatImage,
              productName: product.name,
              category: product.catagory ? product.catagory : product.category,
              price: product.price,
              createdBy: "Admin",
              hasImage: product.hasImage
            },
          ]
        }
        );

      });
    }
  }, [catalog]);

  useEffect(() => {
    if (searchFilterValue.length > 0 || selectedCategory) {

      const filtered = data.filter((item) => {
        return item.productName.toLowerCase().includes(searchFilterValue.toLowerCase()) && item.category === selectedCategory || item.category.toLowerCase().includes(searchFilterValue.toLowerCase()) && item.category === selectedCategory
      })

      setfilteredData(filtered)
    } else {
      setfilteredData([])
    }

  }, [searchFilterValue, selectedCategory])

  return (
    <View style={styles.container}>
      <View style={styles.topRow}>
        <Text style={styles.productManagementTxt}>Product Management</Text>
        <View>
          <TextInput style={styles.searchProductBox} placeholder="Search" value={searchFilterValue} onChangeText={(text) => setsearchFilterValue(text)} />
          <Feather name="search" style={{ color: 'grey', fontSize: 20, position: 'absolute', top: 5, right: 5 }} />
        </View>
        <TouchableOpacity style={styles.manageProductsBtn}>
          <MaterialCommunityIcons
            name="format-list-checks"
            style={styles.manageProductIcon}
          />
          <Text style={styles.manageProductsTxt}>Manage Products</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.categoriesScrollView}>
        <ScrollView
          horizontal={true}
          contentContainerStyle={
            styles.categoriesScrollView_contentContainerStyle
          }
        >
          {catalog.categories.map((category, index) => (
            <TouchableOpacity key={index}
              activeOpacity={0.8}
              style={[{ marginRight: 35 }, selectedCategory === category ? { borderBottomWidth: 2, borderBottomColor: 'black' } : { borderBottomWidth: 2, borderBottomColor: 'grey' }]}
              onPress={() => setselectedCategory(prev => prev === category ? null : category)}>
              <Text style={[styles.categoryOpt1Txt, selectedCategory === category ? { color: 'black' } : { color: 'grey' }]}>{category}</Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>
      <View style={styles.scrollArea}>
        <ScrollView
          contentContainerStyle={styles.scrollArea_contentContainerStyle}
        >
          <View style={styles.productsMap}>
            <TouchableOpacity style={styles.addProductBtn} onPress={() => history.push('addproduct-product')}>
              <Feather
                name="plus"
                style={styles.addProductPlusIcon}
              />
              <Text style={styles.addNewItemTxt}>Add New Item</Text>
            </TouchableOpacity>
            {data.map((product, index) => {
              if (searchFilterValue.length > 0 || selectedCategory) {
                if (item.productName.toLowerCase().includes(searchFilterValue.toLowerCase()) && item.category === selectedCategory || item.category.toLowerCase().includes(searchFilterValue.toLowerCase()) && item.category === selectedCategory) {
                  return (
                    <ProductOptionBox
                      key={index}
                      style={styles.productOptionBox}
                      index={index}
                      product={product}
                    />
                  )
                }
              }else{
                return (
                  <ProductOptionBox
                    key={index}
                    style={styles.productOptionBox}
                    index={index}
                    product={product}
                  />
                )
              }
            })}
          </View>
        </ScrollView>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#ffffff",
    alignItems: "center",
    justifyContent: "flex-start", // Align items to the start to avoid stretching
    width: '100%',
  },
  topRow: {
    width: '95%',
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginTop: 25,
    marginBottom: 25,
  },
  scrollArea: {
    flex: 1, // Take up remaining space
    width: '95%',
  },
  scrollArea_contentContainerStyle: {
    flexGrow: 1, // Allow the container to grow as needed
    justifyContent: "flex-start", // Align items to the start
  },
  productManagementTxt: {
    fontWeight: '700',
    color: "#121212",
    fontSize: 16
  },
  searchProductBox: {
    width: 675,
    height: 34,
    backgroundColor: "#f6f6fb",
    borderWidth: 1,
    borderColor: "#000000",
    borderRadius: 10,
    paddingLeft: 10
  },
  manageProductsBtn: {
    width: 181,
    height: 38,
    backgroundColor: "#fdfdff",
    borderWidth: 1,
    borderColor: "#000000",
    borderRadius: 10,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-around"
  },
  manageProductIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 22
  },
  manageProductsTxt: {
    fontWeight: '700',
    color: "#121212",
    fontSize: 14
  },
  categoriesScrollView: {
    width: '85%',
    marginBottom: 30,
  },
  categoriesScrollView_contentContainerStyle: {
    width: '100%',
  },
  categoryOpt1Txt: {
    // color: "#121212"
    color: 'grey',
    padding: 10
  },
  categoryOpt2Txt: {
    color: "#121212",
    marginLeft: 100
  },
  productsMap: {
    width: '100%',
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-between",
  },
  addProductBtn: {
    width: 215,
    height: 285,
    borderWidth: 3,
    borderColor: "#858585",
    borderRadius: 10,
    borderStyle: "dashed",
    backgroundColor: "#f5f5f5",
    alignItems: "center",
    justifyContent: "center",
    marginRight: 30,
    marginBottom: 30
  },
  addProductPlusIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 25
  },
  addNewItemTxt: {
    color: "#121212",
    fontSize: 16,
    marginTop: 20
  },
  productOptionBox: {
    height: 285,
    width: 215,
    marginLeft: 0,
    marginBottom: 30,
    marginRight: 30
  },
  productOptionBox1: {
    height: 285,
    width: 215,
    marginRight: 30,
    marginLeft: 0,
    marginBottom: 30
  },
  productOptionBox2: {
    height: 285,
    width: 215,
    marginLeft: 0,
    marginBottom: 30,
    marginRight: 30
  },
  productOptionBox3: {
    height: 285,
    width: 215,
    marginLeft: 0,
    marginBottom: 30,
    marginRight: 30
  }
});

export default ProductList;
