import React, { useEffect, useState } from "react";
import { employeesState, myDeviceDetailsState, storeDetailState, woocommerceState } from "state/state";
import { auth, db } from "state/firebaseConfig";
const tz = require("moment-timezone");
import { Button, Modal, Text, TouchableOpacity, View } from "react-native";
import { useHistory } from "react-router-dom";

const EmployeesReport = () => {
    const employees = employeesState.use()
    const history = useHistory();

    return (
        <div className="page-wrapper">
            <div className="content">
                <div className="page-header">
                    <div className="page-title">
                        <h4>Employee Report</h4>
                        <h6>Manage your Employee Report</h6>
                    </div>
                </div>
                {/* /product list */}
                <div className="card">
                    <div className="card-body">
                        <View style={{height: '100%', width: '100%', justifyContent: 'center', alignItems: 'center', }}>
                        <View style={{ flexDirection: 'row', alignItems: 'center', flexWrap: 'wrap', width: '60%', }}>
                            {employees.length > 0 ? employees.map((employee, index) => {
                                return (
                                    <TouchableOpacity key={index} onPress={() => {
                                        history.push(`/authed/report/editemployee/${employee.id}`)
                                    }}
                                        style={{ backgroundColor: 'blue', padding: 10, borderRadius: 5, margin: 10, alignItems: 'center', justifyContent: 'center', width: 125, height: 50 }}
                                    >
                                        <Text>
                                            {employee.name}
                                        </Text>
                                    </TouchableOpacity>
                                )
                            }) : <Text>No Employees</Text>}
                            </View>
                        </View>
                    </div>
                </div>
                {/* /product list */}
            </div>
        </div>
    );
};

export default EmployeesReport;
