import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  Image,
  ScrollView,
} from "react-native";
import EntypoIcon from "@expo/vector-icons/Entypo";
import FeatherIcon from "@expo/vector-icons/Feather";
import IoniconsIcon from "@expo/vector-icons/Ionicons";
import MaterialCommunityIconsIcon from "@expo/vector-icons/MaterialCommunityIcons";
import FontAwesome from "@expo/vector-icons/FontAwesome";
import React, { useState } from "react";
import EditStoreDetails from "components/EditStoreDetails";
import DropDown from "components/DropDown";
import { auth, db } from "state/firebaseConfig";
import { loadStripe } from "@stripe/stripe-js";
import { logout, updateStoreDetails } from "state/firebaseFunctions";
import NewUserPaymentUpdate from "./NewUserPaymentUpdate";
import { Switch, TextInput } from "@react-native-material/core";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import { storeDetailState, woocommerceState } from "state/state";
import Select from "react-select";
const GOOGLE_API_KEY = "AIzaSyDjx4LBIEDNRYKEt-0_TJ6jUcst4a2YON4";

const NewUserPayment = ({ resetLoader }) => {
  const [planType, setplanType] = useState({
    value: "monthly",
    label: "Monthly",
  });
  const [stageNum, setstageNum] = useState(1);
  const storeDetails = storeDetailState.use();
  const [storeName, setstoreName] = useState(storeDetails.name);
  const [phoneNumber, setphoneNumber] = useState(storeDetails.phoneNumber);
  const [address, setaddress] = useState(storeDetails.address);
  const [website, setwebsite] = useState(storeDetails.website);
  const [deliveryPrice, setdeliveryPrice] = useState(
    storeDetails.deliveryPrice
  );
  const [com, setcom] = useState(storeDetails.comSelected);
  const [settingsPassword, setsettingsPassword] = useState(
    storeDetails.settingsPassword
  );
  const wooCredentials = woocommerceState.use();
  const [apiUrl, setapiUrl] = useState(wooCredentials.apiUrl);
  const [ck, setck] = useState(wooCredentials.ck);
  const [cs, setcs] = useState(wooCredentials.cs);
  const [useWoocommerce, setuseWoocommerce] = useState(
    wooCredentials.useWoocommerce
  );

  return (
    <>
      {stageNum === 1 && (
        <NewUserPaymentUpdate
          planType={planType}
          setstageNum={setstageNum}
          currentStageNum={1}
          currentStageLbl="Subscription"
          StageContent={() => {
            return (
              <>
                <View style={styles.planItemContainer}>
                  <View style={styles.pITopContainer}>
                    <View style={styles.group4}>
                      <View style={styles.group24}>
                        <Text style={styles.standard}>STANDARD</Text>
                        <Text style={styles.plan2}>Plan</Text>
                      </View>
                      <View style={styles.group23}>
                        <IoniconsIcon
                          name="pricetags-outline"
                          style={styles.icon16}
                        />
                      </View>
                    </View>
                    <Text style={styles.allYearPayment}>All-Year Payment</Text>
                    <View style={styles.group6}>
                      <Text style={styles.overview1}>
                        $
                        {planType.value === "monthly"
                          ? "50.00 CAD"
                          : "480.00 CAD"}
                      </Text>
                      {planType.value === "monthly" && (
                        <Text style={styles.overview1}>$50.00 CAD</Text>
                      )}
                      {planType.value === "yearly" && (
                        <Text style={styles.overview1}>$480.00 CAD</Text>
                      )}
                      {planType.value === "freeTrial" && (
                        <Text style={styles.overview1}>$480.00 CAD</Text>
                      )}
                      <Text style={styles.monthly5}>/</Text>
                      <Select
                        className="basic-single"
                        classNamePrefix="select"
                        name="Plan"
                        options={[
                          { value: "monthly", label: "Monthly" },
                          { value: "yearly", label: "Yearly" },
                          { value: "freeTrial", label: "Free Trial" },
                          { value: "test", label: "Test" },
                        ]}
                        value={planType}
                        onChange={(e) => setplanType(e)}
                      />
                    </View>
                    {planType.value === "yearly" && (
                      <Text
                        style={{ fontSize: 12, color: "red", marginBottom: 15 }}
                      >
                        Paying anually saves you $10 a month!
                      </Text>
                    )}
                    <View style={styles.group7}>
                      <Text style={styles.cloudBased}>Cloud-Based</Text>
                      <IoniconsIcon
                        name="ios-checkmark"
                        style={styles.checkIcon}
                      />
                    </View>
                    <View style={styles.group7}>
                      <Text style={styles.cloudBased}>Easy to Use</Text>
                      <IoniconsIcon
                        name="ios-checkmark"
                        style={styles.checkIcon}
                      />
                    </View>
                    <View style={styles.group7}>
                      <Text style={styles.cloudBased}>
                        WooCommerce Integration
                      </Text>
                      <IoniconsIcon
                        name="ios-checkmark"
                        style={styles.checkIcon}
                      />
                    </View>
                    <View style={styles.group7}>
                      <Text style={styles.cloudBased}>24/H Support</Text>
                      <IoniconsIcon
                        name="ios-checkmark"
                        style={styles.checkIcon}
                      />
                    </View>
                  </View>
                  <View style={styles.pITBottomContainer}>
                    <View style={styles.rect32} />
                    <View style={styles.rect33} />
                  </View>
                </View>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "space-between",
                    width: "65%",
                  }}
                >
                  <TouchableOpacity style={styles.group5} onPress={logout}>
                    <Text style={styles.monthly6}>Cancel</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={styles.group5}
                    onPress={() => setstageNum((prev) => prev + 1)}
                  >
                    <Text style={styles.monthly6}>Next</Text>
                  </TouchableOpacity>
                </View>
              </>
            );
          }}
        />
      )}
      {stageNum === 2 && (
        <NewUserPaymentUpdate
          planType={planType}
          setstageNum={setstageNum}
          currentStageNum={2}
          currentStageLbl="Store Setup"
          StageContent={() => {
            const [localStoreName, setlocalStoreName] = useState(null);
            const [localStorePhone, setlocalStorePhone] = useState(null);
            const [localStoreWebsite, setlocalStoreWebsite] = useState(null);
            const [localStoreAddress, setlocalStoreAddress] = useState(null);

            return (
              <>
                <View style={styles.planItemContainer}>
                  <View style={styles.pITopContainer}>
                    <View style={styles.group4}>
                      <View style={styles.group24}>
                        <Text style={styles.standard}>Store Details</Text>
                      </View>
                      <View style={styles.group23}>
                        <MaterialCommunityIconsIcon
                          name="store"
                          style={styles.icon16}
                        />
                      </View>
                    </View>
                    <Text style={styles.allYearPayment}>
                      Fill In Your Stores Information!
                    </Text>
                    <TextInput
                      color="black"
                      onChangeText={(val) => setlocalStoreName(val)}
                      value={localStoreName}
                      style={styles.textInput}
                      label="Enter store name"
                      leading={(props) => (
                        <MaterialCommunityIconsIcon name="account" {...props} />
                      )}
                    />
                    <TextInput
                      color="black"
                      onChangeText={(val) => setlocalStorePhone(val)}
                      value={localStorePhone}
                      style={styles.textInput}
                      label="Enter store phone #"
                      leading={(props) => (
                        <MaterialCommunityIconsIcon name="phone" {...props} />
                      )}
                    />
                    <TextInput
                      color="black"
                      onChangeText={(val) => setlocalStoreWebsite(val)}
                      value={localStoreWebsite}
                      style={[styles.textInput, { marginBottom: 15 }]}
                      label="Enter store website url"
                      leading={(props) => (
                        <MaterialCommunityIconsIcon name="web" {...props} />
                      )}
                    />
                    <GooglePlacesAutocomplete
                      apiOptions={{
                        region: "CA",
                      }}
                      debounce={800}
                      apiKey={GOOGLE_API_KEY}
                      // onSelect={handleAddress}
                      selectProps={{
                        localStoreAddress,
                        onChange: setlocalStoreAddress,
                        defaultValue: localStoreAddress,
                        placeholder: "Enter store address",
                        menuPortalTarget: document.body,
                        styles: {
                          menuPortal: (base) => ({ ...base, zIndex: 9999 }),
                        },
                      }}
                      renderSuggestions={(
                        active,
                        suggestions,
                        onSelectSuggestion
                      ) => (
                        <div>
                          {suggestions.map((suggestion) => (
                            <div
                              className="suggestion"
                              onClick={(event) => {
                                onSelectSuggestion(suggestion, event);
                              }}
                            >
                              {suggestion.description}
                            </div>
                          ))}
                        </div>
                      )}
                    />
                  </View>
                  <View style={styles.pITBottomContainer}>
                    <View style={[styles.rect32, { width: "50%" }]} />
                    <View style={[styles.rect33, { width: "50%" }]} />
                  </View>
                </View>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "space-between",
                    width: "65%",
                  }}
                >
                  <TouchableOpacity style={styles.group5} onPress={logout}>
                    <Text style={styles.monthly6}>Cancel</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={styles.group5}
                    onPress={() => {
                      if (
                        localStoreName &&
                        localStorePhone &&
                        localStoreAddress
                      ) {
                        setstageNum((prev) => prev + 1);
                        setstoreName(localStoreName);
                        setphoneNumber(localStorePhone);
                        setaddress(localStoreAddress);
                        setwebsite(localStoreWebsite);
                      } else {
                        alert("Please fill out all required inputs");
                      }
                    }}
                  >
                    <Text style={styles.monthly6}>Next</Text>
                  </TouchableOpacity>
                </View>
              </>
            );
          }}
        />
      )}
      {stageNum === 3 && (
        <NewUserPaymentUpdate
          CheckOutFunc={async () => {
            resetLoader();
            updateStoreDetails({
              name: storeName,
              phoneNumber: phoneNumber,
              address: address,
              website: website ? website : null,
              deliveryPrice: deliveryPrice ? deliveryPrice : null,
              comSelected: com ? com : null,
              settingsPassword: settingsPassword ? settingsPassword : null,
            });

            let priceId;
            if (planType.value === "monthly") {
              priceId = "price_1Mb2s4CIw3L7DOwI5PDx3qKx";
            } else if (planType.value === "yearly") {
              priceId = "price_1Mb2s4CIw3L7DOwIF00zPa4q";
            } else if (planType.value === "test") {
              priceId = "price_1Mb4uSCIw3L7DOwI6exh9JBt";
            }
            await db
              .collection("users")
              .doc(auth.currentUser.uid)
              .collection("checkout_sessions")
              .add({
                price: priceId, // todo price Id from your products price in the Stripe Dashboard
                success_url: window.location.origin, // return user to this screen on successful purchase
                cancel_url: window.location.origin, // return user to this screen on failed purchase
              })
              .then((docRef) => {
                // Wait for the checkoutSession to get attached by the extension
                docRef.onSnapshot(async (snap) => {
                  const { error, sessionId } = snap.data();
                  if (error) {
                    // Show an error to your customer and inspect
                    // your Cloud Function logs in the Firebase console.
                    alert(`An error occurred: ${error.message}`);
                  }

                  if (sessionId) {
                    // We have a session, let's redirect to Checkout
                    // Init Stripe
                    const stripe = await loadStripe(
                      "pk_live_51MHqrvCIw3L7DOwI0ol9CTCSH7mQXTLKpxTWKzmwOY1MdKwaYwhdJq6WTpkWdBeql3sS44JmybynlRnaO2nSa1FK001dHiEOZO" // todo enter your public stripe key here
                    );
                    console.log(`redirecting`);
                    await stripe.redirectToCheckout({ sessionId });
                  }
                });
              });
          }}
          planType={planType}
          setstageNum={setstageNum}
          currentStageNum={3}
          currentStageLbl="Connect Shop"
          StageContent={() => (
            <>
              <View style={styles.planItemContainer}>
                <View style={styles.pITopContainer}>
                  <ScrollView>
                    <View style={styles.group4}>
                      <View style={styles.group24}>
                        <Text style={styles.standard}>Setup Externals</Text>
                      </View>
                      <View style={styles.group23}>
                        <MaterialCommunityIconsIcon
                          name="progress-question"
                          style={styles.icon16}
                        />
                      </View>
                    </View>
                    <Text style={styles.allYearPayment}>
                      Add your woocommerce and set up printer!
                    </Text>
                    <TextInput
                      color="black"
                      style={styles.textInput}
                      label="Enter receipt printer name"
                      leading={(props) => (
                        <MaterialCommunityIconsIcon name="printer" {...props} />
                      )}
                      onChangeText={(val) => setcom(val)}
                      value={com}
                    />
                    <Text style={styles.helperTxt}>
                      Download helper program that makes your printer work with
                      our service
                    </Text>
                    <View style={styles.helperDownloadContainer}>
                      <a
                        href={require("assets/divine-pos-helper.exe")}
                        download="Divine Pos Helper.exe"
                      >
                        <Image
                          source={require("assets/badge-windows.png")}
                          resizeMode="contain"
                          style={styles.badgeWindows}
                        />
                      </a>
                      <a
                        href={require("assets/divine-pos-helper.pkg")}
                        download="Divine Pos Helper.pkg"
                      >
                        <Image
                          source={require("assets/badge-mac.png")}
                          resizeMode="contain"
                          style={styles.badgeMac}
                        />
                      </a>
                    </View>
                    <Text style={{ marginTop: 10, marginBottom: 5 }}>
                      Optionaly connect your WooCommerce store
                    </Text>
                    <Switch
                      value={useWoocommerce}
                      onValueChange={(val) => setuseWoocommerce(val)}
                    />
                    {useWoocommerce && (
                      <View style={{ paddingBottom: 25 }}>
                        <TextInput
                          color="black"
                          style={styles.textInput}
                          label="Enter Woocommerce 'API Url'"
                          onChangeText={(val) => setapiUrl(val)}
                          value={apiUrl}
                        />
                        <TextInput
                          color="black"
                          style={styles.textInput}
                          label="Enter Woocommerce 'CK'"
                          onChangeText={(val) => setck(val)}
                          value={ck}
                        />
                        <TextInput
                          color="black"
                          style={styles.textInput}
                          label="Enter Woocommerce 'CS'"
                          onChangeText={(val) => setcs(val)}
                          value={cs}
                        />
                      </View>
                    )}
                  </ScrollView>
                </View>
                <View style={styles.pITBottomContainer}>
                  <View style={[styles.rect32, { width: "75%" }]} />
                  <View style={[styles.rect33, { width: "25%" }]} />
                </View>
              </View>
              <View
                style={{
                  flexDirection: "row",
                  alignItems: "center",
                  justifyContent: "space-between",
                  width: "65%",
                }}
              >
                <TouchableOpacity style={styles.group5} onPress={logout}>
                  <Text style={styles.monthly6}>Cancel</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.group5, { opacity: 0.5 }]}
                  disabled={true}
                >
                  <Text style={styles.monthly6}>Next</Text>
                </TouchableOpacity>
              </View>
            </>
          )}
        />
      )}
    </>
  );
};

