import React, { useState, useRef } from "react";
import { StyleSheet, View, TouchableOpacity, Text } from "react-native";
import FieldInputWithLabel from "./FieldInputWithLabel";
import {
  CardCvcElement,
  CardExpiryElement,
  CardNumberElement,
  useElements,
  useStripe,
} from "@stripe/react-stripe-js";

function CheckOutDetails({
  storeDetails,
  setorderDetails,
  orderDetails,
  setpage,
}) {
  const [emailAddress, setEmailAddress] = useState("");

  const stripe = useStripe();
  const elements = useElements();

  const cardNumberRef = useRef();
  const cardExpiryRef = useRef();
  const cardCvcRef = useRef();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      console.log("Stripe.js has not yet loaded.");
      return;
    }

    const cardNumberElement = elements.getElement(CardNumberElement);

    const { token, error } = await stripe.createToken(cardNumberElement);

    if (error) {
      console.error("Error creating token:", error);
    } else {
      console.log("Token:", token);

      // Send the token, amount, and currency to your Firebase Function to process the payment
      const response = await fetch(
        "https://us-central1-posmate-5fc0a.cloudfunctions.net/processPayment",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            token: token.id,
            amount: orderDetails.total,
            currency,
            storeUID: storeDetails.docID,
            orderDetails: orderDetails,
          }),
        }
      );

      try {
        const responseData = await response.json();

        if (response.ok && responseData.success) {
          console.log("Payment processed successfully!");
          setpage(4);
        } else {
          console.error(
            "Payment processing failed. Server message:",
            responseData.message
          );
        }
      } catch (jsonError) {
        console.error("Error parsing JSON response:", jsonError);
      }
    }
  };

  const CARD_ELEMENT_OPTIONS = {
    style: {
      base: {
        iconColor: "#c4f0ff",
        color: "#fff",
        fontWeight: "500",
        fontFamily: "Roboto, Open Sans, Segoe UI, sans-serif",
        fontSize: "16px",
        fontSmoothing: "antialiased",
        ":-webkit-autofill": {
          color: "#fce883",
        },
        "::placeholder": {
          color: "#87bbfd",
        },
        backgroundColor: "#f4f4f4", // This assumes your overall background is the color you want
      },
      invalid: {
        iconColor: "#ffc7ee",
        color: "#ffc7ee",
      },
    },
    hidePostalCode: true,
  };

  return (
    <>
      <View style={styles.fieldsGroup}>
        <FieldInputWithLabel
          label="Email Address*"
          style={styles.nameField}
          value={emailAddress}
          onChangeText={(text) => setEmailAddress(text)}
          customInput={null} // Should be a standard TextInput if needed
        />
        <FieldInputWithLabel
          label="Card Number*"
          style={styles.nameField}
          customInput={() => (
            <CardNumberElement options={CARD_ELEMENT_OPTIONS} />
          )}
        />
        <View style={styles.buzzCodeAndPhoneRow}>
          <FieldInputWithLabel
            label="Expiry*"
            style={styles.buzzCodeField}
            customInput={() => (
              <CardExpiryElement
                options={CARD_ELEMENT_OPTIONS}
                ref={cardExpiryRef}
              />
            )}
          />
          <FieldInputWithLabel
            label="CVV*"
            style={styles.phoneNumberField}
            customInput={() => (
              <CardCvcElement options={CARD_ELEMENT_OPTIONS} ref={cardCvcRef} />
            )}
          />
        </View>
      </View>
      <TouchableOpacity
        style={styles.continueBtn}
        onPress={handleSubmit}
        disabled={!stripe}
      >
        <Text style={styles.continueBtnTxt}>CHECKOUT</Text>
      </TouchableOpacity>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    width: 380,
    height: 390,
  },
  fieldsGroup: {
    width: 380,
    height: 325,
    justifyContent: "space-between",
  },
  nameField: {
    height: 70,
    width: 380,
  },
  addressField: {
    height: 70,
    width: 380,
  },
  buzzCodeAndPhoneRow: {
    width: 380,
    height: 70,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  buzzCodeField: {
    height: 70,
    width: 175,
  },
  phoneNumberField: {
    height: 70,
    width: 175,
  },
  continueBtn: {
    width: 219,
    height: 60,
    backgroundColor: "rgba(238,125,67,1)",
    borderRadius: 60,
    justifyContent: "center",
    alignItems: "center",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 30,
    shadowOpacity: 0.2,
    shadowRadius: 10,
  },
  continueBtnTxt: {
    color: "rgba(255,255,255,1)",
    fontSize: 18,
    fontWeight: "700",
  },
});

export default CheckOutDetails;
