import React, { Component, useEffect, useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  TextInput,
  Modal,
} from "react-native";
import Icon from "react-native-vector-icons/Entypo";

function MultipleTimeSelectableOptionGroup({
  style,
  setopenDropdown,
  openDropdown,
  id,
  label,
  isRequired,
  myObjProfile,
  setmyObjProfile,
  index,
  e,
}) {
  const options = e.optionsList;
  const [localMyObjProfile, setlocalMyObjProfile] = useState(myObjProfile);

  const onMinusPress = ({ option, listIndex }) => {
    const newMyObjProfile = structuredClone(localMyObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs
    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    if (
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes > 0
    ) {
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes -=
        1 * thisItemCountsAs;
    }

    setlocalMyObjProfile(newMyObjProfile);
  };

  const onPlusPress = ({ option, listIndex }) => {
    const newMyObjProfile = structuredClone(localMyObjProfile);
    //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs

    const selectedItems = newMyObjProfile.options[index].optionsList.filter(
      (op) => op.selectedTimes > 0
    );

    const thisItemCountsAs = option.countsAs ? parseInt(option.countsAs) : 1;

    let selectedTimesTotal = thisItemCountsAs;

    selectedItems.map((op) => {
      selectedTimesTotal += op.countsAs
        ? parseInt(op.selectedTimes) * parseInt(op.countsAs)
        : parseInt(op.selectedTimes);
    });

    if (
      parseInt(e.numOfSelectable) >= selectedTimesTotal ||
      !e.numOfSelectable ||
      parseInt(e.numOfSelectable) === 0
    ) {
      if (newMyObjProfile.options[index].optionsList[listIndex].selectedTimes) {
        newMyObjProfile.options[index].optionsList[
          listIndex
        ].selectedTimes += 1;
      } else {
        newMyObjProfile.options[index].optionsList[listIndex].selectedTimes = 1;
      }
      setlocalMyObjProfile(newMyObjProfile);
    } else {
      console.log(
        "Didnt Work ",
        "selectedTimesTotal: ",
        selectedTimesTotal,
        " e.numOfSelectable: ",
        e.numOfSelectable
      );
    }
  };

  const dropdownRef = useRef(); // Reference to the original button
  const [dropdownLayout, setDropdownLayout] = useState();

  useEffect(() => {
    dropdownRef.current.measureInWindow((x, y, width, height) => {
      setDropdownLayout({ x, y, width, height });
    });
  }, []);

  const handleKeyDown = (e, option, listIndex) => {
    // Prevent manual editing
    e.preventDefault();

    if (e.key === "ArrowUp") {
      setQuantity((prevQuantity) => Math.min(prevQuantity + 1, 100)); // Assuming 100 is the max value
      handleArrowUp();
    } else if (e.key === "ArrowDown") {
      setQuantity((prevQuantity) => Math.max(prevQuantity - 1, 1)); // Assuming 1 is the min value
      handleArrowDown();
    }
  };

  const handleArrowUp = () => {
    console.log("Arrow Up pressed");
    // Implement the function to be executed on Arrow Up
  };

  const handleArrowDown = () => {
    console.log("Arrow Down pressed");
    // Implement the function to be executed on Arrow Down
  };

  return (
    <View
      style={[styles.container, style, openDropdown === id && { zIndex: 1000 }]}
    >
      <Text style={styles.lbl}>
        {label} {isRequired ? "*" : ""}
      </Text>
      <View ref={dropdownRef} style={{ width: "70%" }}>
        <TouchableOpacity
          style={styles.dropdown}
          onPress={() => {
            console.log("openDropdown", openDropdown, id);
            if (openDropdown === id) {
              setopenDropdown(null);
              setmyObjProfile(localMyObjProfile); // Save the local changes
            } else {
              setopenDropdown(id);
            }
          }}
        >
          <Text style={styles.placeholder}>Select {label}</Text>
          <Icon
            name={
              openDropdown === id ? "chevron-small-up" : "chevron-small-down"
            }
            style={styles.downIcon}
          ></Icon>
        </TouchableOpacity>
        {openDropdown === id && (
          <ScrollView
            scrollEventThrottle={16} // Adjust as needed for performance
            style={{
              width: "100%",
              position: "absolute",
              backgroundColor: "white",
              bottom: options.length > 3 ? -44 * 3 : -44 * options.length,
              height: options.length > 3 ? 44 * 3 : 44 * options.length,
            }}
          >
            {options.map((option, listIndex) => (
              <View
                key={listIndex}
                id={option.id}
                style={{
                  width: "100%",
                  height: 44,
                  backgroundColor: "white",
                  padding: 10,
                  borderWidth: 1,
                  justifyContent: "space-between",
                  alignItems: "center",
                  flexDirection: "row",
                }}
              >
                <TouchableOpacity
                  onPress={() => onMinusPress({ option, listIndex })}
                >
                  <Text>-</Text>
                </TouchableOpacity>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "space-between",
                    width: "80%",
                  }}
                >
                  <Text>
                    {`${option.label} ${
                      option.priceIncrease ? `(+$${option.priceIncrease})` : ""
                    }`}
                  </Text>
                  <TextInput
                    style={{
                      width: 40,
                      height: 30,
                      borderWidth: 1,
                      borderColor: "black",
                      margin: 10,
                    }}
                    value={
                      localMyObjProfile.options[index].optionsList[listIndex]
                        .selectedTimes > 0
                        ? localMyObjProfile.options[index].optionsList[
                            listIndex
                          ].selectedTimes
                        : 0
                    }
                    keyboardType="numeric"
                    // onChangeText={(text) => {
                    //   let newOptions = [...options];
                    //   newOptions[index].selectedTimes = text;
                    //   setoptions(newOptions);
                    // }}
                  />
                </View>
                <TouchableOpacity
                  onPress={() => onPlusPress({ option, listIndex })}
                >
                  <Text>+</Text>
                </TouchableOpacity>
              </View>
            ))}
          </ScrollView>
        )}
      </View>
      <Modal visible={openDropdown === id} transparent={true}>
        <TouchableOpacity
          style={{
            width: "100%",
            height: "100%",
          }}
          onPress={() => {
            setopenDropdown(null);
            setmyObjProfile(localMyObjProfile); // Save the local changes
          }} // Hide modal when the background is pressed
        />
        {dropdownLayout && (
          <View
            style={{
              position: "absolute",
              top: dropdownLayout.y,
              left: dropdownLayout.x,
              width: dropdownLayout.width,
            }}
          >
            <TouchableOpacity
              style={styles.dropdown}
              onPress={() => {
                console.log("openDropdown", openDropdown, id);
                if (openDropdown === id) {
                  setopenDropdown(null);
                  setmyObjProfile(localMyObjProfile); // Save the local changes
                } else {
                  setopenDropdown(id);
                }
              }}
            >
              <Text style={styles.placeholder}>Select {label}</Text>
              <Icon
                name={
                  openDropdown === id
                    ? "chevron-small-up"
                    : "chevron-small-down"
                }
                style={styles.downIcon}
              ></Icon>
            </TouchableOpacity>
            {openDropdown === id && (
              <ScrollView
                scrollEventThrottle={16} // Adjust as needed for performance
                style={{
                  width: "100%",
                  position: "absolute",
                  backgroundColor: "white",
                  bottom: options.length > 3 ? -44 * 3 : -44 * options.length,
                  height: options.length > 3 ? 44 * 3 : 44 * options.length,
                }}
              >
                {options.map((option, listIndex) => (
                  <View
                    key={listIndex}
                    id={option.id}
                    style={{
                      width: "100%",
                      height: 44,
                      backgroundColor: "white",
                      padding: 10,
                      borderWidth: 1,
                      justifyContent: "space-between",
                      alignItems: "center",
                      flexDirection: "row",
                    }}
                  >
                    <TouchableOpacity
                      onPress={() => onMinusPress({ option, listIndex })}
                    >
                      <Text>-</Text>
                    </TouchableOpacity>
                    <View
                      style={{
                        flexDirection: "row",
                        alignItems: "center",
                        justifyContent: "space-between",
                        width: "80%",
                      }}
                    >
                      <Text>
                        {`${option.label} ${
                          option.priceIncrease
                            ? `(+$${option.priceIncrease})`
                            : ""
                        }`}
                      </Text>
                      <input
                        type="number"
                        value={
                          localMyObjProfile.options[index].optionsList[
                            listIndex
                          ].selectedTimes > 0
                            ? localMyObjProfile.options[index].optionsList[
                                listIndex
                              ].selectedTimes
                            : 0
                        }
                        onKeyDown={handleKeyDown}
                        onChange={() => {}}
                        onFocus={(e) => e.target.blur()} // This makes the input blur immediately when focused, effectively making it read-only
                      />
                      <input
                        type="number"
                        id={option.id}
                        name="quantity"
                        value={
                          localMyObjProfile.options[index].optionsList[
                            listIndex
                          ].selectedTimes > 0
                            ? localMyObjProfile.options[index].optionsList[
                                listIndex
                              ].selectedTimes
                            : 0
                        }
                        onChange={(e) => {
                          if (parseInt(e.target.value) >= 0) {
                            const newMyObjProfile =
                              structuredClone(localMyObjProfile);
                            //filter out all options[index].optionsList that have selectedTimes > 0 then map through and multiply by countsAs

                            const selectedItems = newMyObjProfile.options[
                              index
                            ].optionsList.filter((op) => op.selectedTimes > 0);

                            const thisItemCountsAs = option.countsAs
                              ? parseInt(option.countsAs) *
                                parseInt(e.target.value)
                              : parseInt(e.target.value);

                            let selectedTimesTotal = thisItemCountsAs;

                            selectedItems.map((op) => {
                              selectedTimesTotal += op.countsAs
                                ? parseInt(op.selectedTimes) *
                                  parseInt(op.countsAs)
                                : parseInt(op.selectedTimes);
                            });

                            if (
                              parseInt(e.numOfSelectable) >=
                                selectedTimesTotal ||
                              !e.numOfSelectable ||
                              parseInt(e.numOfSelectable) === 0
                            ) {
                              console.log(
                                "selectedTimesTotal: ",
                                selectedTimesTotal,
                                " e.numOfSelectable: ",
                                e.numOfSelectable,
                                " e.target.value: ",
                                e.target.value,
                                " option.countsAs: ",
                                option.countsAs
                              );
                              newMyObjProfile.options[index].optionsList[
                                listIndex
                              ].selectedTimes = parseInt(e.target.value);
                              setlocalMyObjProfile(newMyObjProfile);
                            } else {
                              document.getElementById(option.id).value = "";
                              console.log(
                                "Didnt Work ",
                                "selectedTimesTotal: ",
                                selectedTimesTotal,
                                " e.numOfSelectable: ",
                                e.numOfSelectable
                              );
                            }
                          }
                        }}
                      />
                      {/* <TextInput
