import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import Select2 from "react-select2-wrapper";
import "react-select2-wrapper/css/select2.css";
import { Upload } from "../../EntryFile/imagePath";
import {
  View,
  Text,
  ScrollView,
  useWindowDimensions,
  StyleSheet,
  Image,
  TouchableOpacity,
  Animated,
  Modal,
} from "react-native";
import { Button, Switch, TextInput } from "@react-native-material/core";
import {
  setStoreDetailState,
  setWoocommerceState,
  storeDetailState,
  trialDetailsState,
  woocommerceState,
} from "state/state";
import {
  updateStoreDetails,
  updateWooCredentials,
} from "state/firebaseFunctions";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import Foundation from "@expo/vector-icons/Foundation";
import { auth, db } from "state/firebaseConfig";
import { loadStripe } from "@stripe/stripe-js";
import firebase from "firebase/app";

const GOOGLE_API_KEY = "AIzaSyDjx4LBIEDNRYKEt-0_TJ6jUcst4a2YON4";

const GenaralSettings = () => {
  const options = [
    { id: 1, text: "Choose Time Zone", text: "Choose Time Zone" },
    { id: 2, text: "USD Time Zone", text: "USD Time Zone" },
  ];
  const options1 = [
    { id: 1, text: "INR", text: "INR" },
    { id: 2, text: "USA", text: "USA" },
  ];
  const options2 = [
    { id: 1, text: "DD/MM/YYYY", text: "DD/MM/YYYY" },
    { id: 2, text: "MM/DD/YYYY", text: "MM/DD/YYYY" },
  ];

  const storeDetails = storeDetailState.use();
  const [name, setname] = useState(storeDetails.name);
  const [phoneNumber, setphoneNumber] = useState(storeDetails.phoneNumber);
  const [address, setaddress] = useState(storeDetails.address);
  const [website, setwebsite] = useState(storeDetails.website);
  const [deliveryPrice, setdeliveryPrice] = useState(
    storeDetails.deliveryPrice
  );
  const [com, setcom] = useState(storeDetails.comSelected);
  const [settingsPassword, setsettingsPassword] = useState(
    storeDetails.settingsPassword
  );
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const [viewVisible, setviewVisible] = useState(false);
  const [screen, setscreen] = useState("general");
  const wooCredentials = woocommerceState.use();
  const [apiUrl, setapiUrl] = useState(wooCredentials.apiUrl);
  const [ck, setck] = useState(wooCredentials.ck);
  const [cs, setcs] = useState(wooCredentials.cs);
  const [useWoocommerce, setuseWoocommerce] = useState(
    wooCredentials.useWoocommerce
  );
  const trialDetails = trialDetailsState.use();

  useEffect(() => {
    console.log("Store Details: ", storeDetails);
  }, [storeDetails]);

  const handleWooDataUpdate = () => {
    if (apiUrl !== null && ck !== null && cs !== null) {
      setWoocommerceState({
        apiUrl: apiUrl,
        ck: ck,
        cs: cs,
        useWoocommerce: useWoocommerce,
      });
      updateWooCredentials({
        apiUrl: apiUrl,
        ck: ck,
        cs: cs,
        useWoocommerce: useWoocommerce,
      });
    }
  };

  const fadeIn = () => {
    // Will change fadeAnim value to 0 in 3 seconds
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 500,
      useNativeDriver: false,
    }).start();
  };

  const resetLoader = () => {
    setviewVisible(true);
    fadeIn();
  };

  const Manage = () => {
    resetLoader();
    firebase
      .functions()
      .httpsCallable("ext-firestore-stripe-payments-createPortalLink")({
        returnUrl: `${window.location.origin}`,
        locale: "auto",
      })
      .then((response) => {
        console.log(response.data);
        window.location = response.data.url;
      })
      .catch((error) => {
        alert("Unknown error has occured: ", error);
      });
  };

  const handleDataUpdate = () => {
    if (name !== null && phoneNumber !== null && address !== null) {
      setStoreDetailState({
        name: name,
        phoneNumber: phoneNumber,
        address: address,
        website: website,
        deliveryPrice: deliveryPrice,
        comSelected: com,
        settingsPassword: settingsPassword,
      });
      updateStoreDetails({
        name: name,
        phoneNumber: phoneNumber,
        address: address,
        website: website,
        deliveryPrice: deliveryPrice,
        comSelected: com,
        settingsPassword: settingsPassword,
      });
      if (customBtnExtraFunction) {
        customBtnExtraFunction();
      }
    } else {
      alert("Please fill in all fields");
    }
  };

  return (
    <div className="page-wrapper">
      <div className="content">
        <div className="page-header">
          <div className="page-title">
            <h4>General Setting</h4>
            <h6>Manage General Setting</h6>
          </div>
        </div>
        {/* /add */}
        <div className="card">
          <div className="card-body">
            <div className="row">
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Store Name <span className="manitory">*</span>
                  </label>
                  <input type="text" placeholder={name} />
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Store Address <span className="manitory">*</span>
                  </label>
                  <input type="text" placeholder="Enter Store Address" />
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Store Website Url <span className="manitory">*</span>
                  </label>
                  <input type="text" placeholder={website} />
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Store Phone # <span className="manitory">*</span>
                  </label>
                  <input type="text" placeholder={phoneNumber} />
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Store Delivery Price <span className="manitory">*</span>
                  </label>
                  <input type="text" placeholder={deliveryPrice} />
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Store Printer Name <span className="manitory">*</span>
                  </label>
                  <input type="text" placeholder={com} />
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Dashboard Password <span className="manitory">*</span>
                  </label>
                  <input type="text" placeholder={setsettingsPassword} />
                </div>
              </div>
              {/* <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>Time Zone </label>
                  <Select2
                    className="select"
                    data={options}
                    options={{
                      placeholder: "Choose Time Zone",
                    }}
                  />                  
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Currency <span className="manitory">*</span>
                  </label>
                  <Select2
                    className="select"
                    data={options1}
                    options={{
                      placeholder: "Choose Currency",
                    }}
                  />                   
                </div>
              </div>
              <div className="col-lg-3 col-sm-6 col-12">
                <div className="form-group">
                  <label>
                    Date Format<span className="manitory">*</span>
                  </label>
                  <Select2
                    className="select"
                    data={options2}
                    options={{
                      placeholder: "Choose Date Format",
                    }}
                  />                  
                </div>
              </div> */}
              <div className="row">
                <div className="col-lg-12">
                  <Link style={{ textDecoration: 'none' }} to="#" className="btn btn-submit me-2" onClick={handleDataUpdate}>
                    Update
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* /add */}
      </div>
    </div >
  );
};

export default GenaralSettings;
