import React, { Component } from "react";
import { StyleSheet, View, Image, Text, TouchableOpacity } from "react-native";
import { MaterialCommunityIcons } from "@expo/vector-icons";

function ItemContainer(props) {
  return (
    <View style={[styles.container, props?.style]}>
      <Image
        source={require("../../assets/images/image_ERZz..png")}
        resizeMode="contain"
        style={styles?.itemImg}
      ></Image>
      <View style={styles?.rightSide}>
        <Text style={styles?.familyCombo}>Family Combo</Text>
        <Text style={styles?.price}>$29.99</Text>
        <View style={styles?.openBtnRow}>
          <TouchableOpacity style={styles?.openBtn}>
            <MaterialCommunityIcons
              name="plus"
              style={styles?.plusIcon}
            ></MaterialCommunityIcons>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#ffffff",
    borderRadius: 20,
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
  },
  itemImg: {
    height: 133,
    width: 117,
    margin: 6,
  },
  rightSide: {
    width: 94,
    justifyContent: "space-between",
    margin: 6,
    alignSelf: "stretch",
    marginTop: 12,
    marginRight: 11,
  },
  familyCombo: {
    fontWeight: "700",
    color: "#121212",
    fontSize: 18,
    height: 42,
    alignSelf: "stretch",
    paddingBottom: 50,
  },
  price: {
    fontWeight: "700",
    color: "#00c93b",
    fontSize: 18,
    height: 24,
    alignSelf: "stretch",
  },
  openBtnRow: {
    height: 42,
    alignItems: "flex-end",
    alignSelf: "stretch",
  },
  openBtn: {
    width: 35,
    height: 32,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 100,
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 30,
    shadowOpacity: 0.2,
    shadowRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  plusIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 30,
  },
});

export default ItemContainer;
