    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  goBackIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 40,
  },
  closeIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 40,
  },
  georgesOrders: {
    fontWeight: "700",
    color: "#121212",
    fontSize: 20,
  },
  bottomContainer: {
    width: 454,
    height: 454,
    justifyContent: "space-between",
    alignItems: "flex-start",
  },
  customerDetailsContainer: {
    width: 439,
    height: 157,
    alignItems: "center",
    justifyContent: "space-around",
    borderRadius: 10,
    backgroundColor: "#edf1fe",
  },
  customerPhoneNumberRow: {
    width: 395,
    height: 35,
    flexDirection: "row",
    justifyContent: "flex-start",
    alignItems: "center",
    paddingTop: 0,
    marginTop: 20,
  },
  phoneIcon: {
    color: "#1c294e",
    fontSize: 35,
    paddingRight: 15,
  },
  phoneNumber: {
    color: "#121212",
    fontSize: 15,
  },
  addressRow: {
    width: 395,
    height: 42,
    flexDirection: "row",
    justifyContent: "flex-start",
    alignItems: "center",
    marginTop: 15,
  },
  addressIcon: {
    color: "#1c294e",
    fontSize: 35,
    paddingRight: 10,
  },
  addressGroup: {
    height: 36,
    justifyContent: "space-between",
    alignItems: "center",
  },
  address: {
    color: "#121212",
    fontSize: 15,
  },
  addressExtraDetailsRow: {
    width: 207,
    height: 18,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  unitNumber: {
    color: "#121212",
    fontSize: 15,
  },
  buzzCode: {
    color: "#121212",
    fontSize: 15,
  },
  customerOptionsBtnRow: {
    width: 395,
    height: 35,
    flexDirection: "row",
    justifyContent: "flex-end",
    alignItems: "center",
    marginBottom: 10,
  },
  editCustomerIcon: {
    color: "#1c294e",
    fontSize: 32,
  },
  deleteIcon: {
    color: "#1c294e",
    fontSize: 32,
    paddingLeft: 10,
  },
  orderScrollView: {
    width: 439,
    height: 239,
  },
  orderScrollView_contentContainerStyle: {
    paddingTop: 20,
  },
  addNewOrderRow: {
    width: 439,
    height: 18,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "flex-end",
  },
  addNewOrder: {
    color: "#121212",
    fontSize: 15,
    textDecorationLine: "underline",
  },
});

export default CustomerDetailsModal;
