import React, { Component } from "react";
import { StyleSheet, View, Text } from "react-native";
import ActiveOneTimeSelectableBtn from "./ActiveOneTimeSelectableBtn";
import NonActiveOneTimeSelectableBtn from "./NonActiveOneTimeSelectableBtn";

function OneTimeSelectableOptionGroup(props) {

  return (
    <View style={[styles.container, props.style]}>
      <Text style={styles.lbl}>Size</Text>
      <View style={styles.optionsRow}>
        <ActiveOneTimeSelectableBtn
          style={styles.activeOneTimeSelectableBtn}
        ></ActiveOneTimeSelectableBtn>
        <NonActiveOneTimeSelectableBtn
          style={styles.nonActiveOneTimeSelectableBtn}
        ></NonActiveOneTimeSelectableBtn>
        <NonActiveOneTimeSelectableBtn
          style={styles.nonActiveOneTimeSelectableBtn1}
        ></NonActiveOneTimeSelectableBtn>
        <NonActiveOneTimeSelectableBtn
          style={styles.nonActiveOneTimeSelectableBtn2}
        ></NonActiveOneTimeSelectableBtn>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "flex-start"
  },
  lbl: {
    fontWeight: '700',
    color: "#121212",
    fontSize: 14
  },
  optionsRow: {
    width: 352,
    height: 82,
    flexDirection: "row",
    flexWrap: "wrap"
  },
  activeOneTimeSelectableBtn: {
    height: 33,
    width: 110,
    marginRight: 7,
    marginBottom: 15
  },
  nonActiveOneTimeSelectableBtn: {
    height: 33,
    width: 110,
    marginRight: 7,
    marginBottom: 15
  },
  nonActiveOneTimeSelectableBtn1: {
    height: 33,
    width: 110,
    marginRight: 7,
    marginBottom: 15
  },
  nonActiveOneTimeSelectableBtn2: {
    height: 33,
    width: 110,
    marginRight: 7,
    marginBottom: 15
  }
});

export default OneTimeSelectableOptionGroup;
