import {
  Button,
  SafeAreaView,
  StyleSheet,
  Text,
  TextInput,
  View,
  Dimensions,
  ScrollView,
} from "react-native";
import React, { useEffect, useState } from "react";
import { GooglePlacesAutocomplete } from "react-native-google-places-autocomplete";
import RouteSliderScreen from "./RouteSliderScreen";

const GOOGLE_API_KEY = "AIzaSyB5l-wEA7ueMuvdHqVZK8ZD7-1yMLV076M";

const sh = Dimensions.get("window").height;
const sw = Dimensions.get("window").width;

const App = () => {
  const [listOfAddresses, setlistOfAddresses] = useState([]);
  const [finalList, setfinalList] = useState([]);

  const GooglePlacesInput = ({ placeholder }) => {
    return (
      <GooglePlacesAutocomplete
        placeholder={placeholder}
        fetchDetails={true}
        onPress={(data, details) => {
          setlistOfAddresses([...listOfAddresses, details]);
        }}
        query={{
          key: GOOGLE_API_KEY,
          language: "en",
        }}
        textInputProps={{
          style: {
            minHeight: 40,
            width: sw * 0.8,
            padding: 10,
            fontSize: 15,
            borderWidth: 2,
            borderRadius: 6,
          },
        }}
        onFail={(error) => console.error(error)}
        requestUrl={{
          useOnPlatform: "web", // or "all"
          url: "https://cors-anywhere.herokuapp.com/https://maps.googleapis.com/maps/api", // or any proxy server that hits https://maps.googleapis.com/maps/api
          // headers: {
          //   Authorization: `an auth token`, // if required for your proxy
          // },
        }}
      />
    );
  };

  const DistanceMatrix = async () => {
    const localFinalList = [listOfAddresses[0]];
    const localListOfAddresses = listOfAddresses;

    let myObject = listOfAddresses[0];

    for (let index = 0; index < listOfAddresses.length + 1; index++) {
      const dis = (d) =>
        Math.pow(d.geometry.location.lat, 2) +
        Math.pow(d.geometry.location.lng, 2);

      localListOfAddresses.splice(localListOfAddresses.indexOf(myObject), 1);

      const closest = localListOfAddresses.reduce((a, b) => {
        return Math.abs(dis(b) - dis(myObject)) <
          Math.abs(dis(a) - dis(myObject))
          ? b
          : a;
      });

      myObject = closest;
      localFinalList.push(closest);
    }
    setfinalList(localFinalList);
  };

  return (
    <View
      style={{
        flex: 1,
        height: "100%",
        width: "100%",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {finalList.length < 1 ? (
        <View>
          <Text>Get The Best Route List</Text>
          {listOfAddresses.length < 1 ? (
            <GooglePlacesInput placeholder="Start Address" />
          ) : (
            <View>
              <GooglePlacesInput placeholder="Enter Next Stop" />
              {listOfAddresses.length > 2 && (
                <Button title="Go" onPress={DistanceMatrix} />
              )}
            </View>
          )}
        </View>
      ) : (
        <RouteSliderScreen finalList={finalList} />
      )}
    </View>
  );
};

export default App;

const styles = StyleSheet.create({});
