import { StyleSheet, View, Image, Text, TouchableOpacity } from "react-native";
import Icon from "react-native-vector-icons/Entypo";
import DropDownMenuBtn from "./components/DropDownMenuBtn";
import ActiveDropDownOption from "./components/ActiveDropDownOption";
import NonActiveDropDownOption from "./components/NonActiveDropDownOption";
import React, { useEffect, useState } from "react";
import { Route, withRouter } from "react-router-dom";
import { auth, db } from "state/firebaseConfig";
import {
  setTransListState,
  setTransListTableOrgState,
  woocommerceState,
} from "state/state";

const tz = require("moment-timezone");

function BackendPosContainer(props) {
  const { match } = props;

  const wooCredentials = woocommerceState.use();
  const [transList, setTransList] = useState([]);
  const [transListTableOrg, setTransListTableOrg] = useState([]);

  const getDate = (receipt) => {
    if (receipt.date_created) {
      const dateString = receipt.date_created;

      const newDate = new Date(dateString + "Z");

      const targetTimezone = Intl.DateTimeFormat().resolvedOptions().timeZone;

      const result = tz(newDate)
        .tz(targetTimezone, true)
        .format("YYYY-MM-DD HH:mm a");

      return result;
    } else if (receipt.date) {
      const newDate = new Date(receipt.date.seconds * 1000);
      const targetTimezone = Intl.DateTimeFormat().resolvedOptions().timeZone;

      const result = tz(newDate)
        .tz(targetTimezone, true)
        .format("YYYY-MM-DD HH:mm a");

      return result;
    }
  };

  useEffect(() => {
    try {
      db.collection("users")
        .doc(auth.currentUser?.uid)
        .collection("transList")
        .get()
        .then((querySnapshot) => {
          querySnapshot.forEach((doc) => {
            // doc.data() is never undefined for query doc snapshots
            // console.log(doc.id, " => ", doc.data());
            let orderType = "";
            if (doc.data().method === "deliveryOrder") {
              orderType = "Delivery";
            }
            if (doc.data().method === "pickupOrder") {
              orderType = "Pickup";
            }
            if (doc.data().method === "inStoreOrder") {
              orderType = "In Store";
            }

            setTransList((prevState) => [...prevState, doc.data()]);
            setTransListTableOrg((prevState) => [
              ...prevState,
              {
                ...doc.data(),
                id: doc.data().transNum.toUpperCase(),
                number: doc.data().transNum,
                name: doc.data().customer?.name
                  ? doc.data().customer?.name
                  : "N/A",
                date: getDate(doc.data()),
                originalData: doc.data(),
                amount: doc.data().total,
                system: "POS",
                type: orderType,
              },
            ]);
          });
          //sort by date
          setTransListTableOrg((prevState) => {
            const newList = [
              ...prevState.sort(
                (a, b) =>
                  new Date(
                    b.originalData.date_created
                      ? b.originalData.date_created
                      : b.originalData.date.seconds * 1000
                  ) -
                  new Date(
                    a.originalData.date_created
                      ? a.originalData.date_created
                      : a.originalData.date.seconds * 1000
                  )
              ),
            ];
            setTransList(newList);

            return newList;
          });
        });
    } catch {
      console.log("Error occured retrieving tranasctions");
    }

    if (wooCredentials.useWoocommerce === true) {
      try {
        const WooCommerceAPI = require("woocommerce-api");

        const WooCommerce = new WooCommerceAPI({
          url: wooCredentials.apiUrl,
          consumerKey: wooCredentials.ck,
          consumerSecret: wooCredentials.cs,
          wpAPI: true,
          version: "wc/v1",
        });

        let page = 1;
        let orders = [];

        const getOrders = async () => {
          const response = await WooCommerce.getAsync(
            `orders?page=${page}&per_page=100`
          );
          const data = JSON.parse(response.body);
          orders = [...orders, ...data];
          if (data.length === 100) {
            page++;
            getOrders();
          } else {
            // console.log(orders);
          }
        };

        getOrders()
          .then(() => setTransList((prevState) => [...prevState, ...orders]))
          .catch((e) => console.log("error has occured"));
      } catch {
        console.log("Something occured with woo");
      }
    }
  }, []);

  useEffect(() => {
    setTransListState(transList);
    setTransListTableOrgState(transListTableOrg);
  }, [transList, transListTableOrg]);

  return (
    <>
      <View style={styles.container}>
        <View style={styles.header}>
          <Image
            source={require("./assets/images/image_nrJg..png")}
            resizeMode="contain"
            style={styles.logo}
          ></Image>
          <View style={styles.rightSideRow}>
            <View style={styles.backToPOSBtn}>
              <Text style={styles.pos}>POS</Text>
            </View>
            <TouchableOpacity style={styles.userBtn}>
              <View style={styles.iconWithNameGroup}>
                <Image
                  source={require("./assets/images/image_bTyU..png")}
                  resizeMode="contain"
                  style={styles.userIcon}
                ></Image>
                <Text style={styles.username}>Peter Putros</Text>
              </View>
              <Icon
                name="chevron-small-down"
                style={styles.chevronDownIcon}
              ></Icon>
            </TouchableOpacity>
          </View>
        </View>
        <View style={styles.bottom}>
          <View style={styles.leftMenu}>
            <View style={styles.menuOptionsContainer}>
              <View style={styles.openDropDownBtnContainer}>
                <DropDownMenuBtn
                  style={styles.dropDownMenuBtn}
                ></DropDownMenuBtn>
                <View style={styles.dropDownOptionsContainer}>
                  <ActiveDropDownOption
                    style={styles.activeDropDownOption}
                  ></ActiveDropDownOption>
                  <NonActiveDropDownOption
                    style={styles.nonActiveDropDownOption}
                  ></NonActiveDropDownOption>
                </View>
              </View>
            </View>
          </View>
          <View style={styles.rightSide}>
            <View style={styles.page}>
              {/* {index &&
              index.map((route, key) => (
                <Route
                  key={key}
                  path={`${match.url}/${route.path}`}
                  component={route.component}
                />
              ))} */}
            </View>
          </View>
        </View>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#eef2ff",
  },
  header: {
    height: 75,
    backgroundColor: "rgba(255,255,255,1)",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  logo: {
    height: 75,
    width: 222,
    marginRight: 20,
    marginLeft: 20,
  },
  rightSideRow: {
    width: 358,
    height: 39,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginRight: 20,
    marginLeft: 20,
  },
  backToPOSBtn: {
    width: 140,
    height: 32,
    backgroundColor: "#1c294e",
    borderRadius: 20,
    alignItems: "center",
    justifyContent: "center",
  },
  pos: {
    fontFamily: "roboto-700",
    color: "rgba(255,255,255,1)",
    fontSize: 18,
  },
  userBtn: {
    width: 182,
    height: 39,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  iconWithNameGroup: {
    width: 131,
    height: 39,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  userIcon: {
    height: 39,
    width: 40,
  },
  username: {
    fontFamily: "roboto-regular",
    color: "#435869",
    fontSize: 15,
  },
  chevronDownIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
  },
  bottom: {
    height: 950,
    flexDirection: "row",
    justifyContent: "space-between",
    backgroundColor: "rgba(238,242,255,1)",
  },
  leftMenu: {
    width: 278,
    height: 949,
    alignItems: "center",
    justifyContent: "center",
  },
  menuOptionsContainer: {
    width: 201,
    height: 876,
    alignItems: "center",
    justifyContent: "flex-start",
    marginTop: 0,
    marginRight: 35,
  },
  openDropDownBtnContainer: {
    width: 201,
    height: 137,
    justifyContent: "space-between",
    alignItems: "flex-end",
    marginBottom: 20,
  },
  dropDownMenuBtn: {
    height: 42,
    width: 201,
  },
  dropDownOptionsContainer: {
    width: 179,
    height: 75,
    justifyContent: "space-between",
  },
  activeDropDownOption: {
    height: 34,
    width: 179,
  },
  nonActiveDropDownOption: {
    height: 34,
    width: 179,
  },
  rightSide: {
    width: 1162,
    height: 949,
    justifyContent: "flex-end",
  },
  page: {
    width: 1123,
    height: 913,
    backgroundColor: "#ffffff",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 45,
    shadowOpacity: 0.2,
    shadowRadius: 15,
  },
});

export default withRouter(BackendPosContainer);
