import React, { Component, useState } from "react";
import styled, { css } from "styled-components";
import { FontAwesome, Entypo } from "@expo/vector-icons";
import DeliveryDetails from "./components/home/DeliveryDetails";
import PickupDetails from "./components/home/PickupDetails";
import { Text, View } from "react-native";

function OnlineOrderHome({
  storeDetails,
  setorderDetails,
  orderDetails,
  setpage,
}) {
  const [enterDetailsSection, setenterDetailsSection] = useState(null);
  const [logoImg, setlogoImg] = useState(null);

  // Function to calculate distance between two points using Haversine formula
  function calculateDistance(lat1, lon1, lat2, lon2) {
    const R = 6371; // Radius of the Earth in km
    const dLat = (lat2 - lat1) * (Math.PI / 180); // Convert degrees to radians
    const dLon = (lon2 - lon1) * (Math.PI / 180);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * (Math.PI / 180)) *
        Math.cos(lat2 * (Math.PI / 180)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c; // Distance in km
    return distance;
  }

  // Function to get the latitude and longitude of an address using the Google Maps Geocoding API
  async function getLatLng(placeId) {
    const response = await fetch(
      "https://us-central1-posmate-5fc0a.cloudfunctions.net/getLatLng",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          placeId: placeId,
        }),
      }
    );

    try {
      const responseData = await response.json();

      if (response.ok && responseData.success) {
        console.log("Success!");
        return responseData.data;
      } else {
        console.error(responseData.message);
      }
    } catch (jsonError) {
      console.error("Error parsing JSON response:", jsonError);
    }
  }

  // Function to calculate distance between two addresses using Google Places API
  async function calculateDistanceBetweenAddresses(address1, address2) {
    try {
      const { lat: lat1, lng: lon1 } = await getLatLng(address1);
      const { lat: lat2, lng: lon2 } = await getLatLng(address2);
      const distance = calculateDistance(lat1, lon1, lat2, lon2);
      return distance;
    } catch (error) {
      console.error("Error calculating distance:", error);
      return null;
    }
  }

  //////////////////////////

  return (
    <Container>
      <BackgroundContainer>
        <PlantImg src={require("./assets/images/image_JqcD..png")}></PlantImg>
        <WingImg src={require("./assets/images/image_BSgk..png")} />
        <PizzaImg src={require("./assets/images/image_DrUG..png")}></PizzaImg>
        <FrontContainer>
          <LogoGroup>
            {storeDetails.hasLogo ? (
              <Logo
                onClick={() => setenterDetailsSection(null)}
                src={require("./assets/images/dpos-logo-white.png")}
              ></Logo>
            ) : (
              <Text style={{ fontSize: 35, fontWeight: "700", color: "white" }}>
                {storeDetails.name}
              </Text>
            )}
            <Dash src={require("./assets/images/image_ridw..png")}></Dash>
          </LogoGroup>
          {!enterDetailsSection ? (
            <>
              {!storeDetails.acceptDelivery ? (
                <BtnContainerRow>
                  <PickupBtn onClick={() => setenterDetailsSection("pickup")}>
                    <ButtonOverlay>
                      <PickupBtnTxt>PICK UP</PickupBtnTxt>
                    </ButtonOverlay>
                  </PickupBtn>
                  <DeliveryBtn
                    onClick={() => setenterDetailsSection("delivery")}
                  >
                    <ButtonOverlay>
                      <DeliveryBtnTxt>DELIVERY</DeliveryBtnTxt>
                    </ButtonOverlay>
                  </DeliveryBtn>
                </BtnContainerRow>
              ) : (
                  <BtnContainerRow>
                    <ButtonOverlay>
                      <PickupBtnTxt>PICK UP</PickupBtnTxt>
                    </ButtonOverlay>
                  </BtnContainerRow>

              )}
            </>
          ) : enterDetailsSection === "delivery" ? (
            <DeliveryDetails
              storeDetails={storeDetails}
              setorderDetails={setorderDetails}
              orderDetails={orderDetails}
              setpage={setpage}
            />
          ) : (
            <PickupDetails
              storeDetails={storeDetails}
              setorderDetails={setorderDetails}
              orderDetails={orderDetails}
              setpage={setpage}
            />
          )}
          <BottomRowGroup>
            <DetailsLocationGroup>
              <PhoneNumberRow>
                <FontAwesome
                  name="phone"
                  style={{
                    color: "rgba(255,255,255,1)",
                    fontSize: 50,
                  }}
                ></FontAwesome>
                <PhoneNumberTxt>(226) 600-5925</PhoneNumberTxt>
              </PhoneNumberRow>
              <AddressRow>
                <Entypo
                  name="location-pin"
                  style={{
                    color: "rgba(255,255,255,1)",
                    fontSize: 50,
                  }}
                ></Entypo>
                <AddressTxt>
                  32 Sunrise Drive,{"\n"}Kitchener, Ontario
                </AddressTxt>
              </AddressRow>
            </DetailsLocationGroup>
            <SocialIconsGroup>
              <FacebookIcon
                src={require("./assets/images/image_pDaA..png")}
              ></FacebookIcon>
              <InstagramIcon
                src={require("./assets/images/image_CLpi..png")}
              ></InstagramIcon>
            </SocialIconsGroup>
          </BottomRowGroup>
        </FrontContainer>
      </BackgroundContainer>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  background-color: rgba(30, 30, 30, 1);
  flex-direction: column;
  height: 100vh;
  width: 100vw;
`;

const ButtonOverlay = styled.button`
  display: block;
  background: none;
  height: 100%;
  width: 100%;
  border: none;
`;
const BackgroundContainer = styled.div`
  flex-direction: column;
  display: flex;
  flex: 1 1 0%;
`;

const PlantImg = styled.img`
  width: 200px;
  object-fit: contain;
  position: absolute;
  bottom: 5vh;
  left: 0px;
`;

const WingImg = styled.img`
  width: 40%;
  object-fit: contain;
  bottom: 0px;
  position: absolute;
  margin: auto;
  left: 0;
  right: 0;
  text-align: center;
`;

const PizzaImg = styled.img`
  height: 600px;
  object-fit: contain;
  position: absolute;
