import {
  Modal,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Dimensions,
} from "react-native";
import React, { useEffect, useState } from "react";
import {
  cartState,
  cartSubState,
  setCartState,
  setTransListState,
  storeDetailState,
  transListState,
} from "state/state";
import { Button } from "@react-native-material/core";
import DeliveryScreen from "components/DeliveryScreen";
import CashScreen from "components/CashScreen";
import ChangeScreen from "components/ChangeScreen";
import { updateTransList } from "state/firebaseFunctions";

const CartScreen = () => {
  const [deliveryModal, setDeliveryModal] = useState(false);
  const [cashModal, setCashModal] = useState(false);
  const [changeModal, setChangeModal] = useState(false);
  const [ongoingDelivery, setOngoingDelivery] = useState(null);
  const [name, setName] = useState();
  const [phone, setPhone] = useState();
  const [address, setAddress] = useState();
  const [deliveryChecked, setDeliveryChecked] = useState(false);
  const [changeDue, setChangeDue] = useState();
  const cart = cartState.use();
  const transList = transListState.use();
  const storeDetails = storeDetailState.use();
  const [cartSub, setCartSub] = useState(0);

  useEffect(() => {
    if (cart.length > 0) {
      let newVal = 0;
      for (var i = 0; i < cart.length; i++) {
        newVal += parseFloat(cart[i].price);
      }
      setCartSub(newVal);
    } else {
      setCartSub(0);
    }
  }, [cart]);

  const AddToList = async (payload) => {
    const local = structuredClone(transList);
    local.unshift(payload);
    updateTransList(local);
    //setTransListState(local);
  };

  const Print = (method) => {
    const transNum = transList.length + 1;
    if (method === "deliveryOrder") {
      let total = 5.5;
      const today = new Date();

      let data = [
        "\x1B" + "\x40", // init
        "\x1B" + "\x61" + "\x31", // center align
        storeDetails.name,
        "\x0A",
        storeDetails.address + "\x0A",
        storeDetails.website + "\x0A", // text and line break
        storeDetails.phoneNumber + "\x0A", // text and line break
        today.toLocaleDateString() + " " + today.toLocaleTimeString() + "\x0A",
        "\x0A",
        `Transaction # ${transNum}` + "\x0A",
        "\x0A",
        "Delivery Order: $5.50 Fee" + "\x0A",
        "\x0A",
        "\x0A",
        "\x0A",
        "\x1B" + "\x61" + "\x30", // left align
      ];

      cart.map((cartItem) => {
        total += parseFloat(cartItem.price);
        data.push(`Name: ${cartItem.name}`);
        data.push("\x0A");
        data.push(`Price: $${cartItem.price}`);

        if (cartItem.options) {
          data.push("\x0A");
          cartItem.options.map((option) => {
            data.push(option);
            data.push("\x0A");
          });
        }
        data.push("\x0A" + "\x0A");
      });

      total = total * 1.13;
      total = total.toFixed(2);

      //push ending
      data.push(
        "\x0A",
        "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
        "\x0A" + "\x0A",
        "Customer Name: " + name,
        "\x0A" + "\x0A",
        "Customer Phone #:  " + phone,
        "\x0A" + "\x0A",
        "Customer Address #:  " + address,
        "\x0A" + "\x0A",
        "Total Including (13% Tax): " + total + "\x0A" + "\x0A",
        "------------------------------------------" + "\x0A",
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x1D" + "\x56" + "\x30"
      );
      fetch("http://localhost:8080/print", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          printData: data,
          comSelected: storeDetails.comSelected,
        }),
      })
        .then((response) => response.json())
        .then((respData) => {
          console.log(respData);
        })
        .catch((e) => alert("Error with printer"));

      AddToList({
        date: today,
        transNum: transNum,
        total: total,
        method: "deliveryOrder",
        cart: cart,
      });

      setCartState([]);
      setDeliveryModal(false);
    } else if (method === "pickupOrder") {
      let total = 0;
      const today = new Date();

      let data = [
        "\x1B" + "\x40", // init
        "\x1B" + "\x61" + "\x31", // center align
        storeDetails.name,
        "\x0A",
        storeDetails.address + "\x0A",
        storeDetails.website + "\x0A", // text and line break
        storeDetails.phoneNumber + "\x0A", // text and line break
        today.toLocaleDateString() + " " + today.toLocaleTimeString() + "\x0A",
        "\x0A",
        `Transaction # ${transNum}` + "\x0A",
        "\x0A",
        "Pickup Order" + "\x0A",
        "\x0A",
        "\x0A",
        "\x0A",
        "\x1B" + "\x61" + "\x30", // left align
      ];

      cart.map((cartItem) => {
        total += parseFloat(cartItem.price);
        data.push(`Name: ${cartItem.name}`);
        data.push("\x0A");
        data.push(`Price: $${cartItem.price}`);

        if (cartItem.options) {
          data.push("\x0A");
          cartItem.options.map((option) => {
            data.push(option);
            data.push("\x0A");
          });
        }
        data.push("\x0A" + "\x0A");
      });

      total = total * 1.13;
      total = total.toFixed(2);

      //push ending
      data.push(
        "\x0A",
        "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
        "\x0A" + "\x0A",
        "Customer Name: " + name,
        "\x0A" + "\x0A",
        "Customer Phone #:  " + phone,
        "\x0A" + "\x0A",
        "Total Including (13% Tax): " + total + "\x0A" + "\x0A",
        "------------------------------------------" + "\x0A",
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x1D" + "\x56" + "\x30"
      );
      fetch("http://localhost:8080/print", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          printData: data,
          comSelected: storeDetails.comSelected,
        }),
      })
        .then((response) => response.json())
        .then((respData) => {
          console.log(respData);
        })
        .catch((e) => alert("Error with printer"));

      AddToList({
        date: today,
        transNum: transNum,
        total: total,
        method: "pickupOrder",
        cart: cart,
      });

      setCartState([]);
      setDeliveryModal(false);
    } else {
      let total = 0;
      const today = new Date();

      let data = [
        "\x1B" + "\x40", // init
        "\x1B" + "\x61" + "\x31", // center align
        storeDetails.name,
        "\x0A",
        storeDetails.address + "\x0A",
        storeDetails.website + "\x0A", // text and line break
        storeDetails.phoneNumber + "\x0A", // text and line break
        today.toLocaleDateString() + " " + today.toLocaleTimeString() + "\x0A",
        "\x0A",
        `Transaction # ${transNum}` + "\x0A",
        "\x0A",
        "\x0A",
        "\x0A",
        "\x1B" + "\x61" + "\x30", // left align
      ];

      cart.map((cartItem) => {
        total += parseFloat(cartItem.price);
        data.push(`Name: ${cartItem.name}`);
        data.push("\x0A");
        data.push("\x0A");
        data.push(`Price: $${cartItem.price}`);

        if (cartItem.options) {
          data.push("\x0A");
          cartItem.options.map((option) => {
            data.push(option);
            data.push("\x0A");
          });
        }
        data.push("\x0A" + "\x0A");
      });

      total = total * 1.13;
      total = total.toFixed(2);

      if (method === "Cash") {
        //push ending
        data.push(
          "\x0A",
          "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
          "\x0A" + "\x0A",
          "Payment Method: " + method + "\x0A" + "\x0A",
          "Total Including (13% Tax): " + "$" + total + "\x0A" + "\x0A",
          "Change Due: " + "$" + changeDue + "\x0A" + "\x0A",
          "------------------------------------------" + "\x0A",
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          //"\x1D" + "\x56" + "\x00",
          "\x1D" + "\x56" + "\x30",
          "\x10" + "\x14" + "\x01" + "\x00" + "\x05"
        );
      } else {
        data.push(
          "\x0A",
          "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
          "\x0A" + "\x0A",
          "Payment Method: " + method + "\x0A" + "\x0A",
          "Total Including (13% Tax): " + "$" + total + "\x0A" + "\x0A",
          "------------------------------------------" + "\x0A",
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          //"\x1D" + "\x56" + "\x00",
          "\x1D" + "\x56" + "\x30"
        );
      }

      fetch("http://localhost:8080/print", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          printData: data,
          comSelected: storeDetails.comSelected,
        }),
      })
        .then((response) => response.json())
        .then((respData) => {
          console.log(respData);
        })
        .catch((e) => alert("Error with printer"));

      AddToList({
        date: today,
        transNum: transNum,
        total: total,
        method: method,
        cart: cart,
      });
    }

    setCartState([]);
  };

  const DeliveryBtn = () => {
    if (ongoingDelivery === null) {
      return (
        <Button
          title="Phone Order"
          onPress={() => setDeliveryModal(true)}
          disabled={cart.length > 0}
          style={{ marginBottom: 20 }}
        />
      );
    }
    if (ongoingDelivery && cart.length > 0) {
      return (
        <Button
          title="Complete"
          onPress={() => {
            Print(deliveryChecked ? "deliveryOrder" : "pickupOrder");
            setOngoingDelivery(null);
            setName(null);
            setPhone(null);
            setAddress(null);
          }}
          style={{ marginBottom: 20 }}
        />
      );
    } else {
      return (
        <Button
          title="Cancel"
          onPress={() => setOngoingDelivery(null)}
          style={{ marginBottom: 20 }}
        />
      );
    }
  };

  return (
    <View style={styles.container}>
      <ScrollView
        style={styles.container}
        contentContainerStyle={styles.contentContainer}
      >
        <View>
          {cart.map((cartItem, index) => (
            <View style={{ marginBottom: 20 }}>
              <Text>Name: {cartItem.name}</Text>
              <Text>Price: {cartItem.price}</Text>
              {cartItem.description && (
                <Text>Description: {cartItem.description}</Text>
              )}
              {cartItem.options &&
                cartItem.options.map((option) => <Text>{option}</Text>)}
              <Text
                style={{ color: "blue" }}
                onPress={() => {
                  const local = structuredClone(cart);
                  local.splice(index, 1);
                  setCartState(local);
                }}
              >
                Remove
              </Text>
            </View>
          ))}
        </View>
        <View>
          <Text>Sub: {cartSub.toFixed(2)}</Text>
          <Text>Total: {(cartSub * 1.13).toFixed(2)}</Text>
          <Button
            title="Cash"
            onPress={() => setCashModal(true)}
            disabled={cart.length < 1 || ongoingDelivery}
            style={{ marginBottom: 20 }}
          />
          <Button
            title="Card"
            onPress={() => Print("Card")}
            disabled={cart.length < 1 || ongoingDelivery}
            style={{ marginBottom: 20 }}
          />
          <DeliveryBtn />
          <Button
            title="Change"
            onPress={() => setChangeModal(true)}
            disabled={cart.length > 0 || ongoingDelivery}
          />
        </View>
        <Modal visible={deliveryModal}>
          <DeliveryScreen
            setDeliveryModal={setDeliveryModal}
            setOngoingDelivery={setOngoingDelivery}
            setName={setName}
            setPhone={setPhone}
            setAddress={setAddress}
            name={name}
            phone={phone}
            address={address}
            deliveryChecked={deliveryChecked}
            setDeliveryChecked={setDeliveryChecked}
          />
        </Modal>
        <Modal visible={cashModal}>
          <CashScreen
            setCashModal={setCashModal}
            GetTrans={() => Print("Cash")}
            total={(cartSub * 1.13).toFixed(2)}
            setChangeDue={setChangeDue}
          />
        </Modal>
        <Modal visible={changeModal}>
          <ChangeScreen setChangeModal={setChangeModal} />
        </Modal>
      </ScrollView>
    </View>
  );
};

export default CartScreen;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "lightgrey",
    flex: 1,
    height: "100%",
  },
  contentContainer: {
    backgroundColor: "lightgrey",
    flex: 1,
    height: "100%",
    justifyContent: "space-between",
    padding: 20,
  },
});
