import {
  Modal,
  ScrollView,
  StyleSheet,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useEffect, useState } from "react";
import { addCartState } from "state/state";
import { Button, TextInput, Text, Switch } from "@react-native-material/core";
import { toppings } from "state/allItems";
import ListTopping from "./ListTopping";
import DropDown from "./DropDown";

const BuildPizza = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [item, setItem] = useState({
    size: { name: "small", price: 6.67 },
    toppings: {
      Pepperoni: 0,
      Bacon: 0,
      Ham: 0,
      "Italian Sausage": 0,
      Salami: 0,
      "Ground Beef": 0,
      Chicken: 0,
      Anchovies: 0,
      "Bacon Strips": 0,
      Tomato: 0,
      "Red Onion": 0,
      "Green Olives": 0,
      Mushrooms: 0,
      "Green Peppers": 0,
      "Black Olives": 0,
      Onion: 0,
      Pineapple: 0,
      "Hot Peppers": 0,
      "Artichoke Hearts": 0,
      "Jalapino Peppers": 0,
      "Fresh Garlic": 0,
      Spinach: 0,
      "Red Peppers": 0,
      "Grilled Zucchini": 0,
      "Fried Eggplant": 0,
      "Fresh Basil": 0,
      "Caramelized Onion": 0,
      Mozzarella: 0,
      Cheddar: 0,
      Feta: 0,
      Parmigiano: 0,
      "Fior Di Latte": 0,
    },
    toppings2: {
      Pepperoni: 0,
      Bacon: 0,
      Ham: 0,
      "Italian Sausage": 0,
      Salami: 0,
      "Ground Beef": 0,
      Chicken: 0,
      Anchovies: 0,
      "Bacon Strips": 0,
      Tomato: 0,
      "Red Onion": 0,
      "Green Olives": 0,
      Mushrooms: 0,
      "Green Peppers": 0,
      "Black Olives": 0,
      Onion: 0,
      Pineapple: 0,
      "Hot Peppers": 0,
      "Artichoke Hearts": 0,
      "Jalapino Peppers": 0,
      "Fresh Garlic": 0,
      Spinach: 0,
      "Red Peppers": 0,
      "Grilled Zucchini": 0,
      "Fried Eggplant": 0,
      "Fresh Basil": 0,
      "Caramelized Onion": 0,
      Mozzarella: 0,
      Cheddar: 0,
      Feta: 0,
      Parmigiano: 0,
      "Fior Di Latte": 0,
    },
    quantity: 1,
    crustOption: null,
  });
  const [price, setPrice] = useState(0);
  const [checked, setChecked] = useState(false);

  useEffect(() => {
    getPrice();
  }, [item]);

  const ResetItem = () =>
    setItem((prevState) => ({
      ...prevState,
      toppings2: {
        Pepperoni: 0,
        Bacon: 0,
        Ham: 0,
        "Italian Sausage": 0,
        Salami: 0,
        "Ground Beef": 0,
        Chicken: 0,
        Anchovies: 0,
        "Bacon Strips": 0,
        Tomato: 0,
        "Red Onion": 0,
        "Green Olives": 0,
        Mushrooms: 0,
        "Green Peppers": 0,
        "Black Olives": 0,
        Onion: 0,
        Pineapple: 0,
        "Hot Peppers": 0,
        "Artichoke Hearts": 0,
        "Jalapino Peppers": 0,
        "Fresh Garlic": 0,
        Spinach: 0,
        "Red Peppers": 0,
        "Grilled Zucchini": 0,
        "Fried Eggplant": 0,
        "Fresh Basil": 0,
        "Caramelized Onion": 0,
        Mozzarella: 0,
        Cheddar: 0,
        Feta: 0,
        Parmigiano: 0,
        "Fior Di Latte": 0,
      },
    }));

  const changeSize = (newSize) => {
    if (newSize === "small") {
      setItem((prevState) => ({
        ...prevState,
        size: { name: "small", price: 6.67 },
      }));
    }
    if (newSize === "medium") {
      setItem((prevState) => ({
        ...prevState,
        size: { name: "medium", price: 8.25 },
      }));
    }
    if (newSize === "large") {
      setItem((prevState) => ({
        ...prevState,
        size: { name: "large", price: 8.99 },
      }));
    }
    if (newSize === "extralarge") {
      setItem((prevState) => ({
        ...prevState,
        size: { name: "extralarge", price: 10.5 },
      }));
    }
  };

  const AddTopping = (topping, pizza) => {
    if (pizza === 2) {
      setItem((prevState) => ({
        ...prevState,
        toppings2: {
          ...prevState.toppings2,
          [topping]: prevState.toppings2[topping] + 1,
        },
      }));
    } else {
      setItem((prevState) => ({
        ...prevState,
        toppings: {
          ...prevState.toppings,
          [topping]: prevState.toppings[topping] + 1,
        },
      }));
    }
  };

  const RemoveTopping = (topping, pizza) => {
    if (item.toppings[topping] < 1) return;
    if (pizza === 2) {
      setItem((prevState) => ({
        ...prevState,
        toppings2: {
          ...prevState.toppings2,
          [topping]: prevState.toppings2[topping] - 1,
        },
      }));
    } else {
      setItem((prevState) => ({
        ...prevState,
        toppings: {
          ...prevState.toppings,
          [topping]: prevState.toppings[topping] - 1,
        },
      }));
    }
  };

  const AddQuantity = () => {
    setItem((prevState) => ({
      ...prevState,
      quantity: item.quantity + 1,
    }));
  };

  const RemoveQuantity = () => {
    if (item.quantity === 1) return;
    setItem((prevState) => ({
      ...prevState,
      quantity: item.quantity - 1,
    }));
  };

  const getPrice = () => {
    let numberOfToppings = 0;
    let cost = 0;

    for (const property in item.toppings) {
      numberOfToppings += item.toppings[property];
      numberOfToppings += item.toppings2[property];
      // console.log(`${property}: ${item.toppings[property]}`);
    }

    cost += item.size.price;
    if (item.size.name === "small") {
      cost += numberOfToppings * 1.11;
    } else if (item.size.name === "medium") {
      cost += numberOfToppings * 1.58;
    } else if (item.size.name === "large") {
      cost += numberOfToppings * 1.99;
    } else if (item.size.name === "extralarge") {
      cost += numberOfToppings * 2.5;
    }
    setPrice(() => cost * item.quantity);
  };

  const AddToCart = () => {
    let toppings = [];
    let toppings2 = [];

    for (const property in item.toppings) {
      if (item.toppings[property] > 0) {
        toppings.push(`${item.toppings[property]} ${property}`);
      }
      // console.log(`${property}: ${item.toppings[property]}`);
    }

    for (const property in item.toppings2) {
      if (item.toppings2[property] > 0) {
        toppings2.push(`${item.toppings2[property]} ${property}`);
      }
      // console.log(`${property}: ${item.toppings[property]}`);
    }

    if (toppings2.length > 0) {
      addCartState({
        name: "Build Your Own Pizza",
