                      ...orderDetails,
                      delivery: false,
                      address: null,
                    });
                    setenterDetailsSection(null);
                  }
                }}
              >
                <Text
                  style={[
                    { fontSize: 35, fontWeight: "700", color: "white" },
                    screenWidth < 1000 && { fontSize: 30 },
                  ]}
                >
                  {storeDetails.name}
                </Text>
              </TouchableOpacity>
            )}
            <Dash src={require("./assets/images/image_ridw..png")} />
          </LogoGroup>
          {!enterDetailsSection ? (
            <>
              {screenWidth > 1000 ? (
                <>
                  {storeDetails.acceptDelivery ? (
                    <BtnContainerRow>
                      <PickupBtn
                        onClick={() => setenterDetailsSection("pickup")}
                      >
                        <ButtonOverlay>
                          <PickupBtnTxt>PICK UP</PickupBtnTxt>
                        </ButtonOverlay>
                      </PickupBtn>
                      <DeliveryBtn
                        onClick={() => {
                          setorderDetails({ ...orderDetails, delivery: true });
                          setenterDetailsSection("delivery");
                        }}
                      >
                        <ButtonOverlay>
                          <DeliveryBtnTxt>DELIVERY</DeliveryBtnTxt>
                        </ButtonOverlay>
                      </DeliveryBtn>
                    </BtnContainerRow>
                  ) : (
                    <div
                      style={{
                        width: "100%",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                      }}
                    >
                      <PickupBtn
                        onClick={() => setenterDetailsSection("pickup")}
                      >
                        <ButtonOverlay>
                          <PickupBtnTxt>PICK UP</PickupBtnTxt>
                        </ButtonOverlay>
                      </PickupBtn>
                    </div>
                  )}
                </>
              ) : (
                <>
                  {storeDetails.acceptDelivery ? (
                    <View
                      style={{
                        width: "100%",
                        justifyContent: "center",
                        alignItems: "center",
                      }}
                    >
                      <PickupBtn
                        onClick={() => setenterDetailsSection("pickup")}
                      >
                        <ButtonOverlay>
                          <PickupBtnTxt>PICK UP</PickupBtnTxt>
                        </ButtonOverlay>
                      </PickupBtn>
                      <div style={{ height: 30 }}></div>
                      <DeliveryBtn
                        onClick={() => {
                          setorderDetails({ ...orderDetails, delivery: true });
                          setenterDetailsSection("delivery");
                        }}
                      >
                        <ButtonOverlay>
                          <DeliveryBtnTxt>DELIVERY</DeliveryBtnTxt>
                        </ButtonOverlay>
                      </DeliveryBtn>
                    </View>
                  ) : (
                    <div
                      style={{
                        width: "100%",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                      }}
                    >
                      <PickupBtn
                        onClick={() => setenterDetailsSection("pickup")}
                      >
                        <ButtonOverlay>
                          <PickupBtnTxt>PICK UP</PickupBtnTxt>
                        </ButtonOverlay>
                      </PickupBtn>
                    </div>
                  )}
                </>
              )}
            </>
          ) : (
            <>
              {screenWidth > 1000 ? (
                <RenderDetailsSection />
              ) : (
                <View
                  style={{
                    justifyContent: "center",
                    alignItems: "center",
                    width: "100%",
                  }}
                >
                  <RenderDetailsSection />
                </View>
              )}
            </>
          )}
          <BottomRowGroup>
            <DetailsLocationGroup>
              {screenWidth > 1000 ? (
                <>
                  <PhoneNumberRow>
                    <FontAwesome
                      name="phone"
                      style={{
                        color: "rgba(255,255,255,1)",
                        fontSize: 50,
                      }}
                    ></FontAwesome>
                    <PhoneNumberTxt>{storeDetails.phoneNumber}</PhoneNumberTxt>
                  </PhoneNumberRow>
                  <AddressRow>
                    <Entypo
                      name="location-pin"
                      style={{
                        color: "rgba(255,255,255,1)",
                        fontSize: 50,
                      }}
                    ></Entypo>
                    <AddressTxt>
                      {
                        storeDetails.address?.value.structured_formatting
                          .main_text
                      }
                      {"\n"}
                      {
                        storeDetails.address?.value.structured_formatting
                          .secondary_text
                      }
                    </AddressTxt>
                  </AddressRow>
                </>
              ) : (
                <>
                  <PhoneNumberRowMobile>
                    <FontAwesome
                      name="phone"
                      style={{
                        color: "rgba(255,255,255,1)",
                        fontSize: 35,
                      }}
                    ></FontAwesome>
                    <PhoneNumberTxtMobile>
                      {storeDetails.phoneNumber}
                    </PhoneNumberTxtMobile>
                  </PhoneNumberRowMobile>
                  <AddressRowMobile>
                    <Entypo
                      name="location-pin"
                      style={{
                        color: "rgba(255,255,255,1)",
                        fontSize: 35,
                      }}
                    ></Entypo>
                    <AddressTxtMobile>
                      {
                        storeDetails.address?.value.structured_formatting
                          .main_text
                      }
                      {"\n"}
                      {
                        storeDetails.address?.value.structured_formatting
                          .secondary_text
                      }
                    </AddressTxtMobile>
                  </AddressRowMobile>
                </>
              )}
            </DetailsLocationGroup>
            {storeDetails.hasSocial && (
              <SocialIconsGroup>
                <FacebookIcon
                  src={require("./assets/images/image_pDaA..png")}
                ></FacebookIcon>
                <InstagramIcon
                  src={require("./assets/images/image_CLpi..png")}
                ></InstagramIcon>
              </SocialIconsGroup>
            )}
          </BottomRowGroup>
        </FrontContainer>
      </BackgroundContainer>
    </Container>
  );
}

