import { StyleSheet, Text, View } from "react-native";
import React, { useEffect, useState } from "react";
import MenuScreen from "./MenuScreen";
import CartScreen from "./CartScreen";
import { Button } from "@react-native-material/core";
import { sectionState, userStoreState } from "state/state";
import { auth } from "state/firebaseConfig";

const HomeScreen = ({ navigation }) => {
  const catalog = userStoreState.use();
  const section = sectionState.use();

  const Menu = () => {

    return (
      <View
        style={{
          width: "100%",
          height: 120,
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "space-evenly",
          backgroundColor: "red",
        }}
      >
        {catalog.categories?.map((category, index) => {
          if (!section && index === 0) {
            return (
              <Button
                key={index}
                titleStyle={{ color: "white", fontWeight: "700" }}
                style={{ backgroundColor: "blue" }}
                onPress={() => {
                  setsectionState(category);
                }}
                title={category}
              />
            );
          }

          return (
            <Button
              key={index}
              titleStyle={
                section === category
                  ? { color: "white", fontWeight: "700" }
                  : { color: "black", fontWeight: "700" }
              }
              style={
                section === category
                  ? { backgroundColor: "blue" }
                  : { backgroundColor: "white" }
              }
              onPress={() => {
                setsectionState(category);
                localStorage.setItem("localScreen", category);
              }}
              title={category}
            />
          );
        })}
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => navigation.navigate('SettingsHome')}
          title="Settings"
        />
      </View>
    );
  };

  return (
    <View style={{ flexDirection: "row", height: "100%" }}>
      <View>
        <Menu />
        <MenuScreen catalog={catalog} section={section} navigation={navigation} />
        </View>
      <CartScreen />
    </View>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
    flex: 1,
  },
});
