      }

      if (discountAmount) {
        if (discountAmount.includes("%")) {
          const discount = parseFloat(discountAmount.replace("%", "")) / 100;
          setCartSub(parseFloat(newVal) - parseFloat(newVal) * discount);
        } else {
          setCartSub(parseFloat(newVal) - parseFloat(discountAmount));
        }
      } else {
        setCartSub(newVal);
      }
    } else {
