                  discountAmount
                    ? discountAmount.includes("%")
                      ? discountAmount
                      : `$${discountAmount}`
                    : "N/A"
                }
                amountLbl="Discount"
                style={styles.discountRow}
              />
              {deliveryChecked && parseFloat(storeDetails.deliveryPrice) && (
                <UntitledComponent
                  amountValue={`$${parseFloat(
                    storeDetails.deliveryPrice
                  ).toFixed(2)}`}
                  amountLbl="Delivery"
                  style={styles.discountRow}
                />
              )}
              <UntitledComponent
                amountValue={
                  deliveryChecked &&
                  parseFloat(storeDetails.deliveryPrice) &&
                  cartSub > 0
                    ? `$${(
                        cartSub - parseFloat(storeDetails.deliveryPrice)
                      ).toFixed(2)}`
                    : `$${cartSub.toFixed(2)}`
                }
                amountLbl="Subtotal"
                style={styles.subtotalRow}
              />
              <UntitledComponent
                amountValue={`$${(
                  cartSub *
                  (storeDetails.taxRate ? storeDetails.taxRate / 100 : 0.13)
                ).toFixed(2)}`}
                amountLbl="Tax"
                style={styles.taxRow}
              />
            </View>
            <View style={styles.totalRowGroup}>
              <View style={styles.totalRow}>
                <Text style={styles.total2}>Total</Text>
                <Text style={styles.totalValue}>
                  $
                  {(
                    Math.ceil(
                      cartSub *
                        (storeDetails.taxRate
                          ? 1 + storeDetails.taxRate / 100
                          : 1.13) *
                        10
                    ) / 10
                  ).toFixed(2)}
                </Text>
              </View>
              <TouchableOpacity
                style={styles.discountCodeBtn}
                onPress={() => setdiscountModal(true)}
              >
                <Text style={styles.discountCode}>Discount Code</Text>
              </TouchableOpacity>
            </View>
          </View>
          <CheckoutBtn />
        </View>
      ) : (
        <Modal
          isVisible={cartOpen}
          animationIn="slideInUp"
          animationOut="slideOutDown"
          style={{ margin: 0 }}
        >
          <View
            style={{
              width: width,
              height: height,
              justifyContent: "space-evenly",
              alignItems: "center",
              backgroundColor: "white",
            }}
          >
            <View
              style={{
                flexDirection: "row",
                width: "90%",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 10,
              }}
            >
              <TouchableOpacity
                onPress={() => {
                  setcartOpen(false);
                }}
              >
                <Feather
                  onPress={() => {
                    setcartOpen(false);
                  }}
                  name="chevron-down"
                  style={{ color: "grey" }}
                  size={40}
                />
              </TouchableOpacity>
              <View
                style={{
                  backgroundColor: "#1D294E",
                  borderRadius: 10,
                  justifyContent: "space-between",
                  alignItems: "center",
                  width: 80,
                  height: 40,
                  flexDirection: "row",
                  padding: 15,
                }}
              >
                <Feather
                  name="shopping-cart"
                  style={{ color: "white" }}
                  size={22}
                />
                <Text style={{ color: "white", fontSize: 20 }}>
                  {cart.length}
                </Text>
              </View>
            </View>
            <Text style={styles.myCartTxt}>My Cart</Text>
            {cart.length > 0 ? (
              <View style={styles.cartItems}>
                <ScrollView
                  horizontal={false}
                  contentContainerStyle={styles.cartItems_contentContainerStyle}
                  nativeID="scroll"
                >
                  {cart?.map((cartItem, index) => (
                    <CartItem
                      style={styles.cartItem1}
                      key={index}
                      cartItem={cartItem}
                      index={index}
                      removeAction={() => {
                        console.log("Removing");
                        const local = structuredClone(cart);
                        local.splice(index, 1);
                        setCartState(local);
                      }}
                      decreaseAction={() => {
                        const local = structuredClone(cart);
                        local[index].quantity--;
                        setCartState(local);
                      }}
                      increaseAction={() => {
                        const local = structuredClone(cart);
                        if (local[index].quantity) {
                          local[index].quantity++;
                        } else {
                          local[index].quantity = 2;
                        }
                        setCartState(local);
                      }}
                    />
                  ))}
                </ScrollView>
              </View>
            ) : (
              <Image
                source={require("./assets/images/noItemsImg.png")}
                style={{ width: 200, height: "35%", resizeMode: "contain" }}
              />
            )}
            <View style={[styles.totalsContainer, { height: 150 }]}>
              <View style={styles.topGroupTotalsContainer}>
                {deliveryChecked && parseFloat(storeDetails.deliveryPrice) && (
                  <UntitledComponent
                    amountValue={`$${parseFloat(
                      storeDetails.deliveryPrice
                    ).toFixed(2)}`}
                    amountLbl="Delivery"
                    style={styles.discountRow}
                  />
                )}
                <UntitledComponent
                  amountValue={
                    deliveryChecked &&
                    parseFloat(storeDetails.deliveryPrice) &&
                    cartSub > 0
                      ? `$${(
                          cartSub - parseFloat(storeDetails.deliveryPrice)
                        ).toFixed(2)}`
                      : `$${cartSub.toFixed(2)}`
                  }
                  amountLbl="Subtotal"
                  style={styles.subtotalRow}
                />
                <UntitledComponent
                  amountValue={`$${(
                    cartSub *
                    (storeDetails.taxRate ? storeDetails.taxRate / 100 : 0.13)
                  ).toFixed(2)}`}
                  amountLbl="Tax"
                  style={styles.taxRow}
                />
                <View style={styles.totalRow}>
                  <Text style={styles.total2}>Total</Text>
                  <Text style={styles.totalValue}>
                    $
                    {(
                      Math.ceil(
                        cartSub *
                          (storeDetails.taxRate
                            ? 1 + storeDetails.taxRate / 100
                            : 1.13) *
                          10
                      ) / 10
                    ).toFixed(2)}
                  </Text>
                </View>
              </View>
            </View>
            <CheckoutBtn />
          </View>
        </Modal>
      )}
      <Modal
        isVisible={ongoingOrderListModal}
        animationIn="fadeIn"
        animationOut="fadeOut"
      >
        <View
          style={{
            flex: 1,
            height: "100%",
            width: "100%",
            position: "absolute",
            left: 0,
            top: 0,
            justifyContent: "center",
            alignItems: "center",
          }}
