import React, { useState, useRef } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  TextInput,
  Modal,
  TouchableWithoutFeedback,
} from "react-native";
import Icon from "react-native-vector-icons/Entypo";

function MultipleTimeSelectableOptionGroup({
  style,
  setopenDropdown,
  openDropdown,
  id,
  label,
  isRequired,
  myObjProfile,
  setmyObjProfile,
  index,
  e,
}) {
  const [localMyObjProfile, setlocalMyObjProfile] = useState(myObjProfile);
  const options = e.optionsList;
  const scrollViewRef = useRef();

  const onMinusPress = ({ option, listIndex }) => {
    const newMyObjProfile = { ...localMyObjProfile };
    const thisItemCountsAs = option.countsAs
      ? parseInt(option.countsAs, 10)
      : 1;

    if (
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes > 0
    ) {
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes -=
        thisItemCountsAs;
    }

    setlocalMyObjProfile(newMyObjProfile);
  };

  const onPlusPress = ({ option, listIndex }) => {
    const newMyObjProfile = { ...localMyObjProfile };
    const selectedItems = newMyObjProfile.options[index].optionsList.filter(
      (op) => op.selectedTimes > 0
    );

    const thisItemCountsAs = option.countsAs
      ? parseInt(option.countsAs, 10)
      : 1;
    let selectedTimesTotal = thisItemCountsAs;

    selectedItems.forEach((op) => {
      selectedTimesTotal += op.countsAs
        ? parseInt(op.selectedTimes, 10) * parseInt(op.countsAs, 10)
        : parseInt(op.selectedTimes, 10);
    });

    if (
      parseInt(e.numOfSelectable, 10) >= selectedTimesTotal ||
      !e.numOfSelectable ||
      parseInt(e.numOfSelectable, 10) === 0
    ) {
      newMyObjProfile.options[index].optionsList[listIndex].selectedTimes =
        (newMyObjProfile.options[index].optionsList[listIndex].selectedTimes ||
          0) + 1;
    }

    setlocalMyObjProfile(newMyObjProfile);
  };

  const closeDropdown = () => {
    setopenDropdown(null);
    setmyObjProfile(localMyObjProfile); // Save local changes to the main state
  };

  return (
    <>
      {openDropdown === id && (
        <Modal
          transparent={true}
          visible={true}
          animationType="none"
          onRequestClose={closeDropdown}
        >
          <TouchableWithoutFeedback onPress={closeDropdown}>
            <View style={styles.modalOverlay} />
          </TouchableWithoutFeedback>
        </Modal>
      )}
      <View style={[styles.container, style]}>
        <Text style={styles.lbl}>
          {label} {isRequired ? "*" : ""}
        </Text>
        <TouchableOpacity
          style={styles.dropdown}
          onPress={() => {
            setopenDropdown(openDropdown === id ? null : id);
          }}
        >
          <Text style={styles.placeholder}>Select {label}</Text>
          <Icon
            name={
              openDropdown === id ? "chevron-small-up" : "chevron-small-down"
            }
            style={styles.downIcon}
          />
        </TouchableOpacity>
        {openDropdown === id && (
          <ScrollView
            ref={scrollViewRef}
            style={styles.scrollView}
            scrollEventThrottle={16}
          >
            {options.map((option, listIndex) => (
              <View key={listIndex} style={styles.optionContainer}>
                <TouchableOpacity
                  onPress={() => onMinusPress({ option, listIndex })}
                >
                  <Text>-</Text>
                </TouchableOpacity>
                <Text>
                  {`${option.label} ${
                    option.priceIncrease ? `(+$${option.priceIncrease})` : ""
                  }`}
                </Text>
                <TextInput
                  style={styles.quantityInput}
                  value={String(
                    localMyObjProfile.options[index].optionsList[listIndex]
                      .selectedTimes || 0
                  )}
                  keyboardType="numeric"
                  onChangeText={(text) => {
                    const newMyObjProfile = { ...localMyObjProfile };
                    const newText = parseInt(text, 10) || 0;
                    newMyObjProfile.options[index].optionsList[
                      listIndex
                    ].selectedTimes = newText;
                    setlocalMyObjProfile(newMyObjProfile);
                  }}
                />
                <TouchableOpacity
                  onPress={() => onPlusPress({ option, listIndex })}
                >
                  <Text>+</Text>
                </TouchableOpacity>
              </View>
            ))}
          </ScrollView>
        )}
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  modalOverlay: {
    flex: 1,
    backgroundColor: "rgba(0, 0, 0, 0.5)", // You can change this to 'transparent' if you don't want an overlay
  },
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  lbl: {
    fontWeight: "700",
    color: "#3e3f41",
  },
  dropdown: {
    width: 352,
    height: 44,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
    borderWidth: 2,
    borderColor: "#6987d3",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    padding: 10,
  },
  placeholder: {
    fontWeight: "500",
    color: "#7f838c",
    fontSize: 16,
  },
  downIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 20,
    marginRight: 5,
  },
  scrollView: {
    width: 352,
    maxHeight: 132, // Adjusted for 3 items, 44 height each
    position: "absolute",
    backgroundColor: "white",
    zIndex: 1000,
  },
  optionContainer: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: "#ccc",
  },
  quantityInput: {
    borderWidth: 1,
    borderColor: "#ccc",
    padding: 5,
    width: 40,
    textAlign: "center",
  },
  // ... Add other styles that you might need ...
});

export default MultipleTimeSelectableOptionGroup;
