                  titleStyle={{ textAlign: "center" }}
                />
              </View>
            </View>
          </>
        )}
      </View>
      {viewVisible && (
        <Modal visible={true}>
          <Animated.View
            style={{
              alignItems: "center",
              justifyContent: "center",
              backgroundColor: "white",
