import React, { Component, useState } from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import Icon from "react-native-vector-icons/Entypo";

function DropdownSelectableOption(props) {
  const [open, setopen] = useState(false);
const [val, setval] = useState(null)

  const options = [
    { id: 1, name: "Thin" },
    { id: 2, name: "Thick" },
    { id: 3, name: "Stuffed" },
  ]

  return (
    <>
      <TouchableOpacity style={[styles.container, props.style]}>
        <Text style={styles.lbl}>Crust</Text>
        <View style={styles.dropdown}>
          <Text style={styles.placeholder}>Select</Text>
          <Icon name="chevron-small-down" style={styles.downIcon}></Icon>
        </View>
      </TouchableOpacity>
      {open && (
        <View>
          {options.map((option) => (
            <TouchableOpacity
              key={option.id}
              onPress={() => {
                setval(option.name);
                setopen(false);
              }}
            >
              <Text>{option.name}</Text>
            </TouchableOpacity>
          ))}
        </View>    
      )
      }
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  lbl: {
    fontWeight: "700",
    color: "#3e3f41",
  },
  dropdown: {
    width: 352,
    height: 44,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
    borderWidth: 2,
    borderColor: "#6987d3",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "flex-start",
  },
  placeholder: {
    fontWeight: "500",
    color: "#7f838c",
    fontSize: 12,
    margin: 10,
  },
  downIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    margin: 0,
    marginTop: 2,
    marginRight: 2,
  },
});

export default DropdownSelectableOption;
