import React, { useState } from 'react'
import { Upload } from '../../EntryFile/imagePath';
import Select2 from 'react-select2-wrapper';
import 'react-select2-wrapper/css/select2.css';

const AddProduct = () => {
    const catalog = userStoreState.use();
    const [newProduct, setnewProduct] = useState(
        existingProduct
            ? existingProduct
            : {
                name: "",
                price: "",
                category: productCategory,
                options: [],
                description: "",
                id: Math.random().toString(36).substr(2, 9),
            }
    );
    const newProductOptions = useRef(
        existingProduct ? existingProduct.options : []
    );
    const [indexOn, setindexOn] = useState(0);
    const { width, height } = useWindowDimensions();

    // useEffect(() => {
    //   //setTimeout(() => {
    //   window.scrollTo({
    //     top: currentY,
    //     behavior: "instant",
    //   });
    //   //}, 500);
    // }, [currentY]);

    function handleDataUpdate(copyProductData) {
        if (copyProductData) {
            updateData(
                [...catalog.categories],
                [...catalog.products, copyProductData]
            );
            goBack();
        } else if (existingProduct) {
            let copy = structuredClone(catalog.products);

            if (existingProduct.id) {
                const newProductUseRef = {
                    ...newProduct,
                    options: newProductOptions.current,
                };
                const findIndex = copy.findIndex((e) => e.id === existingProduct.id);
                copy[findIndex] = newProductUseRef;
            } else {
                const newProductUseRef = {
                    ...newProduct,
                    options: newProductOptions.current,
                    id: Math.random().toString(36).substr(2, 9),
                };
                copy[existingProductIndex] = newProductUseRef;
            }
            updateData([...catalog.categories], copy);
            goBack();
        } else {
            updateData([...catalog.categories], [...catalog.products, newProduct]);
            goBack();
        }
    }

    return (
        <>
            <div className="page-wrapper">
                <div className="content">
                    <div className="page-header">
                        <div className="page-title">
                            <h4>Product Add</h4>
                            <h6>Create new product</h6>
                        </div>
                    </div>
                    {/* /add */}
                    <div className="card">
                        <div className="card-body">
                            <div className="row">
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Product Name</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Category</label>
                                        <Select2
                              className="select"
                              data={options}
                              options={{
                                placeholder: 'Choose Category',
                              }} />

                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Sub Category</label>
                                        <Select2
                              className="select"
                              data={options1}
                              options={{
                                placeholder: 'Choose Sub Category',
                              }} />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Brand</label>
                                        <Select2
                              className="select"
                              data={options2}
                              options={{
                                placeholder: 'Choose Brand',
                              }} />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Unit</label>
                                        <Select2
                              className="select"
                              data={options3}
                              options={{
                                placeholder: 'Choose Unit',
                              }} />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>SKU</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Minimum Qty</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Quantity</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div className="col-lg-12">
                                    <div className="form-group">
                                        <label>Description</label>
                                        <textarea className="form-control" defaultValue={""} />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Tax</label>
                                        <Select2
                              className="select"
                              data={options4}
                              options={{
                                placeholder: 'Choose Tax',
                              }} />

                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Discount Type</label>
                                        <Select2
                              className="select"
                              data={options5}
                              options={{
                                placeholder: 'Percentage',
                              }} />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Price</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label> Status</label>
                                        <Select2
                              className="select"
                              data={options6}
                              options={{
                                placeholder: 'Choose Product',
                              }} />
                                    </div>
                                </div>
                                <div className="col-lg-12">
                                    <div className="form-group">
                                        <label> Product Image</label>
                                        <div className="image-upload">
                                            <input type="file" />
                                            <div className="image-uploads">
                                                <img src={Upload} alt="img" />
                                                <h4>Drag and drop a file to upload</h4>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-12">
                                    <button className="btn btn-submit me-2">
                                        Submit
                                    </button>
                                    <button className="btn btn-cancel">
                                        Cancel
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* /add */}
                </div>
            </div>
        </>
    )
}
export default AddProduct;
