import { StyleSheet, Text, TextInput, View } from "react-native";
import React, { useEffect, useState } from "react";
import MenuScreen from "./MenuScreen";
import CartScreen from "./CartScreen";
import { Button } from "@react-native-material/core";
import ViewTransactions from "./ViewTransactions";
import { db } from "state/firebaseConfig";
import { userState } from "state/state";

const HomeScreen = () => {
  const user = userState.use();
  const [section, setSection] = useState();

  useEffect(() => {
    const unsubscribe = db
      .collection("users")
      .doc(user.uid)
      .onSnapshot((doc) => {
        console.log("Current data: ", doc.data());
      });

    return () => unsubscribe()
  }, [third])


  const Menu = () => {
    return (
      <View
        style={{
          width: "100%",
          height: 120,
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "space-evenly",
          backgroundColor: "red",
        }}
      >
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("combo")}
          title="Combos"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("specialty")}
          title="Specialty"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("sides")}
          title="Sides"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("deserts")}
          title="Deserts"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("salads")}
          title="Salads"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("sandwhiches")}
          title="Sandwhiches and Wraps"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("sauces")}
          title="Sauces"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("drinks")}
          title="Drinks"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => setSection("transList")}
          title="Transaction"
        />
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <Menu />
      {section === "transList" ? (
        <View style={{ height: "85%" }}>
          <ViewTransactions />
        </View>
      ) : (
        <View style={{ flexDirection: "row", height: "85%" }}>
          <MenuScreen setSection={setSection} section={section} />
          <CartScreen />
        </View>
      )}
    </View>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
    flex: 1,
  },
});
